from abc import ABC, abstractmethod


class NetworkDevice(ABC):
    @abstractmethod
    def connect(self):
        pass

    @abstractmethod
    def transfer_data(self, data: str):
        pass

    @abstractmethod
    def run_diagnostics(self):
        pass

    @abstractmethod
    def update_firmware(self, version: str):
        pass

    @abstractmethod
    def get_device_name(self):
        pass

    @abstractmethod
    def display_info(self):
        pass

class MultimediaDevice(ABC):
    @abstractmethod
    def power_on(self):
        pass

    @abstractmethod
    def play(self):
        pass

    @abstractmethod
    def stop(self):
        pass

    @abstractmethod
    def set_volume(self, level: int):
        pass

    @abstractmethod
    def set_screen_resolution(self, resolution: str):
        pass

    @abstractmethod
    def get_device_name(self):
        pass

    @abstractmethod
    def display_info(self):
        pass


class Router(NetworkDevice):
    def __init__(self, name, ip_address):
        self.name = name
        self.ip_address = ip_address

    def connect(self):
        print(f"Router {self.name} established connection.")

    def transfer_data(self, data):
        print(f"Router transferring data: {data}")

    def run_diagnostics(self):
        print(f"Running diagnostics on router {self.name}")

    def update_firmware(self, version):
        print(f"Router firmware updated to version: {version}")

    def get_device_name(self):
        return self.name

    def display_info(self):
        print(f"Router: {self.name}, IP: {self.ip_address}")

class NetworkSwitch(NetworkDevice):
    def __init__(self, name, ip_address):
        self.name = name
        self.ip_address = ip_address

    def connect(self):
        print(f"Switch {self.name} established connection.")

    def transfer_data(self, data):
        print(f"Switch transferring data: {data}")

    def run_diagnostics(self):
        print(f"Running diagnostics on switch {self.name}")

    def update_firmware(self, version):
        print(f"Switch firmware updated to version: {version}")

    def get_device_name(self):
        return self.name

    def display_info(self):
        print(f"Network Switch: {self.name}, IP: {self.ip_address}")

class SmartTV(MultimediaDevice):
    def __init__(self, name, location):
        self.name = name
        self.location = location

    def power_on(self):
        print(f"{self.name} TV powered on.")

    def play(self):
        print(f"{self.name} TV playing content.")

    def stop(self):
        print(f"{self.name} TV stopped playback.")

    def set_volume(self, level):
        print(f"{self.name} TV volume set to {level}")

    def set_screen_resolution(self, resolution):
        print(f"{self.name} TV resolution set to {resolution}")

    def get_device_name(self):
        return self.name

    def display_info(self):
        print(f"Smart TV: {self.name}, Location: {self.location}")

class SmartSpeaker(MultimediaDevice):
    def __init__(self, name, location):
        self.name = name
        self.location = location

    def power_on(self):
        print(f"{self.name} speaker powered on.")

    def play(self):
        print(f"{self.name} speaker playing audio.")

    def stop(self):
        print(f"{self.name} speaker stopped playback.")

    def set_volume(self, level):
        print(f"{self.name} speaker volume set to {level}")

    def set_screen_resolution(self, resolution):
        print(f"ERROR: {self.name} speaker doesn't have a screen!")

    def get_device_name(self):
        return self.name

    def display_info(self):
        print(f"Smart Speaker: {self.name}, Location: {self.location}")


class DeviceManager:
    def configure_device(self, device):
        if isinstance(device, NetworkDevice):
            device.display_info()
            device.connect()
        elif isinstance(device, MultimediaDevice):
            device.display_info()
            device.power_on()

    def update_firmware_network(self, device: NetworkDevice):
        print(f"Updating firmware for {device.get_device_name()}")
        device.update_firmware("v2.4.1")

    def play_media_multimedia(self, device: MultimediaDevice):
        print(f"Playing media on {device.get_device_name()}")
        device.play()

    def adjust_volume(self, device: MultimediaDevice, level: int):
        print(f"Adjusting volume on {device.get_device_name()}")
        device.set_volume(level)

    def play_media_network(self, device: NetworkDevice):
        print(f"Attempting to play media on {device.get_device_name()}")
        print("ERROR: Network devices cannot play media!")

    def update_firmware_multimedia(self, device: MultimediaDevice):
        print(f"Attempting to update firmware on {device.get_device_name()}")
        print("ERROR: Cannot update firmware on multimedia devices!")

