    fun sendSlackMessage(channel: String, message: String)
}

interface NotificationReceiver {
    fun receiveEmail(sender: String, subject: String, body: String)
    fun receiveSMS(sender: String, message: String)
    fun receivePushNotification(appId: String, title: String, message: String)
    fun receiveSlackMessage(sender: String, message: String)
}

class EmailService : NotificationSender {
    override fun sendEmail(recipient: String, subject: String, body: String) {
        println("Sending email to $recipient with subject: $subject")
    }

    override fun sendSMS(phoneNumber: String, message: String) {
        throw UnsupportedOperationException("EmailService does not support SMS")
    }

    override fun sendPushNotification(deviceId: String, title: String, message: String) {
        throw UnsupportedOperationException("EmailService does not support push notifications")
    }

    override fun sendSlackMessage(channel: String, message: String) {
        throw UnsupportedOperationException("EmailService does not support Slack messages")
    }
}

class SMSService : NotificationSender {
    override fun sendEmail(recipient: String, subject: String, body: String) {
        throw UnsupportedOperationException("SMSService does not support email")
    }

    override fun sendSMS(phoneNumber: String, message: String) {
        println("Sending SMS to $phoneNumber: $message")
    }

    override fun sendPushNotification(deviceId: String, title: String, message: String) {
        throw UnsupportedOperationException("SMSService does not support push notifications")
    }

    override fun sendSlackMessage(channel: String, message: String) {
        throw UnsupportedOperationException("SMSService does not support Slack messages")
    }
}

class EmailClient : NotificationReceiver {
    override fun receiveEmail(sender: String, subject: String, body: String) {
        println("Received email from $sender with subject: $subject")
    }

    override fun receiveSMS(sender: String, message: String) {
        throw UnsupportedOperationException("EmailClient does not handle SMS")
    }

    override fun receivePushNotification(appId: String, title: String, message: String) {
        throw UnsupportedOperationException("EmailClient does not handle push notifications")
    }

    override fun receiveSlackMessage(sender: String, message: String) {
        throw UnsupportedOperationException("EmailClient does not handle Slack messages")
    }
}

