using System;

public interface NotificationSender
{
    void SendEmail(string recipient, string subject, string body);
    void SendSMS(string phoneNumber, string message);
    void SendPushNotification(string deviceId, string title, string message);
    void SendSlackMessage(string channel, string message);
}

public interface NotificationReceiver
{
    void ReceiveEmail(string sender, string subject, string body);
    void ReceiveSMS(string sender, string message);
    void ReceivePushNotification(string appId, string title, string message);
    void ReceiveSlackMessage(string sender, string message);
}

public class EmailService : NotificationSender
{
    public void SendEmail(string recipient, string subject, string body)
    {
        Console.WriteLine("Sending email to " + recipient + " with subject: " + subject);
    }

    public void SendSMS(string phoneNumber, string message)
    {
        throw new NotSupportedException("EmailService does not support SMS");
    }

    public void SendPushNotification(string deviceId, string title, string message)
    {
        throw new NotSupportedException("EmailService does not support push notifications");
    }

    public void SendSlackMessage(string channel, string message)
    {
        throw new NotSupportedException("EmailService does not support Slack messages");
    }
}

public class SMSService : NotificationSender
{
    public void SendEmail(string recipient, string subject, string body)
    {
        throw new NotSupportedException("SMSService does not support email");
    }

    public void SendSMS(string phoneNumber, string message)
    {
        Console.WriteLine("Sending SMS to " + phoneNumber + ": " + message);
    }

    public void SendPushNotification(string deviceId, string title, string message)
    {
        throw new NotSupportedException("SMSService does not support push notifications");
    }

    public void SendSlackMessage(string channel, string message)
    {
        throw new NotSupportedException("SMSService does not support Slack messages");
    }
}

public class EmailClient : NotificationReceiver
{
    public void ReceiveEmail(string sender, string subject, string body)
    {
        Console.WriteLine("Received email from " + sender + " with subject: " + subject);
    }

    public void ReceiveSMS(string sender, string message)
    {
        throw new NotSupportedException("EmailClient does not handle SMS");
    }

    public void ReceivePushNotification(string appId, string title, string message)
    {
        throw new NotSupportedException("EmailClient does not handle push notifications");
    }

    public void ReceiveSlackMessage(string sender, string message)
    {
        throw new NotSupportedException("EmailClient does not handle Slack messages");
    }
}

