import java.lang.UnsupportedOperationException;

class FileProcessor {

    public String getStatus(String filePath) {
        File file = new File(filePath);
        return file.exists() ? "Ready for processing" : "File not found";
    }


    public void processFile(String filePath) {
        File file = new File(filePath);
        if (!file.exists()) {
            System.out.println("Base: Error - File not found for processing.");
            return;
        }
        System.out.println("Base: File processed successfully.");
    }
}

class ReadOnlyFileProcessor extends FileProcessor {

    @Override
    public String getStatus(String filePath) {
        File file = new File(filePath);
        return file.exists() ? "Available (Read-only)" : "File not found (Read-only)";
    }



    @Override
    public void processFile(String filePath) {
        throw new UnsupportedOperationException("Cannot process (write to) a file using a ReadOnlyFileProcessor.");
    }
}

