import java.io.File;
import java.util.HashMap;
import java.util.Map;

class DataProcessor {
    protected Map<String, Object> data = null;

    public DataProcessor() {
        System.out.println("DataProcessor created.");
    }


    public boolean loadData(String source) {
        System.out.println("Base: Loading data from " + source + "...");

        try { Thread.sleep(100); } catch (InterruptedException e) { Thread.currentThread().interrupt(); }
        if ("valid_source".equals(source)) {
            data = new HashMap<>();
            data.put("key1", "value1");
            data.put("key2", 42);
            System.out.println("Base: Data loaded successfully.");
            return true;
        } else {
            data = null;
            System.out.println("Base: Failed to load data.");
            return false;
        }
    }


    public boolean processData() {
        System.out.println("Base: Processing data...");
        if (data == null) {
            System.out.println("Base: No data to process.");
            return false;
        }

        try { Thread.sleep(200); } catch (InterruptedException e) { Thread.currentThread().interrupt(); }
        data.put("processed", true);
        System.out.println("Base: Data processed.");
        return true;
    }


    public boolean saveResults(String destination) {
        System.out.println("Base: Saving results to " + destination + "...");
        if (data == null || !Boolean.TRUE.equals(data.get("processed"))) {
            System.out.println("Base: No processed data to save.");
            return false;
        }

        try { Thread.sleep(300); } catch (InterruptedException e) { Thread.currentThread().interrupt(); }

        System.out.println("Base: Results saved to " + destination + ".");
        return true;
    }
}

class ReadOnlyDataProcessor extends DataProcessor {

    public ReadOnlyDataProcessor() {
        super();
        System.out.println("ReadOnlyDataProcessor created.");



    }





    @Override
    public boolean saveResults(String destination) {
        System.out.println("ReadOnly: Attempted to save results to " + destination + ".");



        throw new UnsupportedOperationException("Cannot save results with a ReadOnlyDataProcessor.");
    }
}

