public abstract class Account
{
    public decimal Balance { get; protected set; } = 1000m;

    public void Withdraw(decimal amount)
    {
        if (CanWithdraw(amount))
        {
            Balance -= amount;
            Console.WriteLine($"Successfully withdrew {amount}. New Balance: {Balance}");
        }
        else
        {
            Console.WriteLine($"Insufficient funds for withdrawal ({amount}). Balance remains: {Balance}");
        }
    }

    protected abstract bool CanWithdraw(decimal amount);
}

public class BaseAccount : Account
{
    protected override bool CanWithdraw(decimal amount) => amount <= Balance;
}

public class LimitedWithdrawalAccount : Account
{
    private const decimal MaxWithdrawalLimit = 50m;

    protected override bool CanWithdraw(decimal amount) => amount > 0 && amount <= MaxWithdrawalLimit && amount <= Balance;
}
```
