using System;
using System.Collections.Generic;
using System.Linq;

public class CollectionProcessor
{
    public virtual void ProcessCollection(List<string> data)
    {
        Console.WriteLine("[BaseProcessor] Starting processing...");
        foreach (var item in data)
        {
            Console.WriteLine($"[BaseProcessor] Processing item: {item}");
            System.Threading.Thread.Sleep(10);
        }
        Console.WriteLine("[BaseProcessor] Processing finished.");
    }
}

public class ModifyingCollectionProcessor : CollectionProcessor
{
    public override void ProcessCollection(List<string> data)
    {
        Console.WriteLine("[ViolatingProcessor] Starting processing...");
        for (int i = 0; i < data.Count; i++)
        {
             var item = data[i];
             Console.WriteLine($"[ViolatingProcessor] Processing item: {item}");
             System.Threading.Thread.Sleep(10);

             if (item.StartsWith("B"))
             {
                 Console.WriteLine($"[ViolatingProcessor] Removing item: {item}");
                 data.RemoveAt(i);
                 i--;
             }
        }
        Console.WriteLine("[ViolatingProcessor] Processing finished. List size after modification: " + data.Count);
    }
}

