import java.text.SimpleDateFormat
import java.util.*

class SalesData(private val sales: List<Sale>) {
    fun getSales(): List<Sale> {
        return sales
    }
}

class Sale(private val productName: String, private val amount: Double, private val date: Date, private val region: String) {
    fun getProductName(): String {
        return productName
    }

    fun getAmount(): Double {
        return amount
    }

    fun getDate(): Date {
        return date
    }

    fun getRegion(): String {
        return region
    }
}

class ReportGenerator {
    fun generateReport(reportType: String, data: SalesData): String {
        val report = StringBuilder()

        when (reportType) {
            "daily" -> {
                report.append("DAILY SALES REPORT\n")
                report.append("===================\n\n")

                val dailySales = mutableMapOf<String, Double>()
                val dateFormat = SimpleDateFormat("yyyy-MM-dd")

                for (sale in data.getSales()) {
                    val dateStr = dateFormat.format(sale.getDate())
                    dailySales[dateStr] = (dailySales[dateStr] ?: 0.0) + sale.getAmount()
                }

                for ((dateStr, amount) in dailySales) {
                    report.append("Date: $dateStr, Total: $$$amount\n")
                }

                val total = dailySales.values.sum()
                report.append("\nTotal Sales: $$$total")
            }
            "product" -> {
                report.append("PRODUCT SALES REPORT\n")
                report.append("====================\n\n")

                val productSales = mutableMapOf<String, Double>()

                for (sale in data.getSales()) {
                    productSales[sale.getProductName()] = (productSales[sale.getProductName()] ?: 0.0) + sale.getAmount()
                }

                for ((product, amount) in productSales) {
                    report.append("Product: $product, Total: $$$amount\n")
                }

                val total = productSales.values.sum()
                report.append("\nTotal Sales: $$$total")
            }
            "regional" -> {
                report.append("REGIONAL SALES REPORT\n")
                report.append("=====================\n\n")

                val regionalSales = mutableMapOf<String, Double>()

                for (sale in data.getSales()) {
                    regionalSales[sale.getRegion()] = (regionalSales[sale.getRegion()] ?: 0.0) + sale.getAmount()
                }

                for ((region, amount) in regionalSales) {
                    report.append("Region: $region, Total: $$$amount\n")
                }

                val total = regionalSales.values.sum()
                report.append("\nTotal Sales: $$$total")
            }
        }

        return report.toString()
    }
}

