    {
        Dictionary<string, double> rates = new Dictionary<string, double>();
        rates.Add("luxury", 0.03);
        rates.Add("food", 0.0);
        return rates;
    }

    private Dictionary<string, double> GetCountryVatRates()
    {
        Dictionary<string, double> rates = new Dictionary<string, double>();
        rates.Add("DE", 0.19);
        rates.Add("FR", 0.20);
        rates.Add("IT", 0.22);
        return rates;
    }

    private Dictionary<string, double> GetProvinceTaxRates()
    {
        Dictionary<string, double> rates = new Dictionary<string, double>();
        rates.Add("ON", 0.08);
        rates.Add("BC", 0.07);
        rates.Add("AB", 0.0);
        return rates;
    }
}

