using System;
using System.Collections.Generic;
using System.Text.RegularExpressions;

public class Content
{
    private string text;
    private string type;
    private Dictionary<string, string> metadata;

    public Content(string text, string type, Dictionary<string, string> metadata)
    {
        this.text = text;
        this.type = type;
        this.metadata = metadata;
    }

    public string GetText()
    {
        return text;
    }

    public string GetType()
    {
        return type;
    }

    public Dictionary<string, string> GetMetadata()
    {
        return metadata;
    }
}

public class ContentFilter
{
    public string FilterContent(Content content)
    {
        string filteredText = content.GetText();

        if (content.GetType().Equals("email"))
        {
            filteredText = FilterEmailContent(filteredText, content.GetMetadata());

        }
        else if (content.GetType().Equals("social"))
        {
            filteredText = FilterSocialContent(filteredText, content.GetMetadata());

        }
        else if (content.GetType().Equals("comment"))
        {
            filteredText = FilterCommentContent(filteredText, content.GetMetadata());
        }

        filteredText = RemoveCommonProfanity(filteredText);
        filteredText = RemovePersonalInfo(filteredText);

        return filteredText;
    }

    private string FilterEmailContent(string text, Dictionary<string, string> metadata)
    {
        string audience = "general";
        if (metadata.ContainsKey("audience"))
        {
            audience = metadata["audience"];
        }

        if (audience.Equals("public"))
        {
            text = Regex.Replace(text, "(?i)confidential:", "[REDACTED]");
            text = Regex.Replace(text, "(?i)internal use only", "[INTERNAL]");
        }

        text = Regex.Replace(text, "(?i)from:\s.*?\n", "");
        text = Regex.Replace(text, "(?i)to:\s.*?\n", "");
        text = Regex.Replace(text, "(?i)cc:\s.*?\n", "");

        return text;
    }

    private string FilterSocialContent(string text, Dictionary<string, string> metadata)
    {
        string platform = "general";
        if (metadata.ContainsKey("platform"))
        {
            platform = metadata["platform"];
        }

        string audience = "general";
        if (metadata.ContainsKey("audience"))
        {
            audience = metadata["audience"];
        }

        if (platform.Equals("twitter"))
        {
            text = Regex.Replace(text, "@([A-Za-z0-9_]+)", "[USERNAME]");
        }
        else if (platform.Equals("facebook"))
        {
            text = Regex.Replace(text, "@\\[[^\\]]+\\]", "[NAME]");
        }

        if (audience.Equals("minor"))
        {
            text = Regex.Replace(text, "(?i)alcohol|beer|wine", "[AGE-RESTRICTED]");
        }

        return text;
    }

    private string FilterCommentContent(string text, Dictionary<string, string> metadata)
    {
        string context = "general";
        if (metadata.ContainsKey("context"))
        {
            context = metadata["context"];
        }

        if (context.Equals("news"))
        {
            text = Regex.Replace(text, "(?i)allegedly|reportedly", "[STATEMENT]");
        }
        else if (context.Equals("review"))
        {
            text = Regex.Replace(text, "(?i)terrible|awful|worst ever", "not satisfactory");
        }

        return text;
    }

    private string RemoveCommonProfanity(string text)
    {
        text = Regex.Replace(text, "(?i)badword1|badword2|badword3", "[INAPPROPRIATE]");
        return text;
    }

    private string RemovePersonalInfo(string text)
    {
        text = Regex.Replace(text, "\d{3}-\d{2}-\d{4}", "[SSN]");
        text = Regex.Replace(text, "\d{16}", "[CREDIT_CARD]");
        return text;
    }
}

