public class DocumentService {
    private final Logger logger;
    private final SecurityService securityService;
    private final DocumentRepository documentRepository;

    public DocumentService(Logger logger, SecurityService securityService, DocumentRepository documentRepository) {
        this.logger = logger;
        this.securityService = securityService;
        this.documentRepository = documentRepository;
    }

    public void createDocument(String title, String content, User creator) {
        Document document = new Document(title, content, creator);
        documentRepository.save(document);
        logger.log("Document created: " + document.getId());
    }

    public void shareDocument(Document document, User user) {
        if (securityService.canShareDocument(document, user)) {
            document.addSharedUser(user);
            documentRepository.update(document);
            logger.log("Document shared: " + document.getId());
        } else {
            throw new IllegalArgumentException("User does not have permission to share the document");
        }
    }
}

public class NotificationService {
    private final User user;

    public NotificationService(User user) {
        this.user = user;
    }

    public void notifyUser() {
        String preferredContactMethod = user.getPreferredContactMethod();
        switch (preferredContactMethod) {
            case "email":
                // send email notification
                break;
            case "sms":
                // send sms notification
                break;
            default:
                throw new IllegalArgumentException("Unsupported contact method");
        }
    }
}

