class Customer(private val name: String, private val email: String) {
    fun getName(): String {
        return name
    }

    fun getEmail(): String {
        return email
    }
}

interface CustomerRepository {
    fun save(customer: Customer)
    fun findByEmail(email: String): Customer?
}

interface EmailService {
    fun sendEmail(to: String, subject: String, body: String)
}

class CustomerService(private val repository: CustomerRepository, private val emailService: EmailService) {

    fun registerCustomer(name: String, email: String, customerType: String): Customer {
        validateCustomerData(name, email)

        val customer = Customer(name, email)
        repository.save(customer)


        sendWelcomeEmail(customer, customerType)

        return customer
    }

    private fun validateCustomerData(name: String, email: String) {
        if (name.isBlank()) {
            throw IllegalArgumentException("Name cannot be empty")
        }

        if (email.isBlank() || !email.matches(Regex("^[A-Za-z0-9+_.-]+@(.+)$"))) {
            throw IllegalArgumentException("Invalid email format")
        }

        if (repository.findByEmail(email) != null) {
            throw IllegalArgumentException("Email already registered")
        }
    }

    private fun sendWelcomeEmail(customer: Customer, customerType: String) {
        val subject = "Welcome to Our Service"
        val body: String

        when (customerType) {
            "standard" -> {
                body = "Dear ${customer.getName()},\n\n" +
                      "Welcome to our service! We're excited to have you join us.\n\n" +
                      "Your account has been created successfully. You can now access all standard features.\n\n" +
                      "Best regards,\n" +
                      "The Service Team"
            }
            "premium" -> {
                body = "Dear ${customer.getName()},\n\n" +
                      "Welcome to our PREMIUM service! We're thrilled to have you as a premium member.\n\n" +
                      "Your premium account has been activated with all exclusive features.\n" +
                      "Our premium support team is available 24/7 to assist you.\n\n" +
                      "Best regards,\n" +
                      "The Premium Service Team"
            }
            "trial" -> {
                body = "Dear ${customer.getName()},\n\n" +
                      "Thank you for trying our service! Your 30-day trial has begun.\n\n" +
                      "Explore all the features we offer during your trial period.\n" +
                      "We'll send you a reminder before your trial expires.\n\n" +
                      "Best regards,\n" +
                      "The Service Team"
            }
            else -> throw IllegalArgumentException("Unsupported customer type: $customerType")
        }

        emailService.sendEmail(customer.getEmail(), subject, body)
    }
}

