    fun getMetadata(): Map<String, String>
}

class TextContent(
    private val text: String,
    private val type: String,
    private val metadata: Map<String, String>
) : Content {
    override fun getText(): String = text
    override fun getType(): String = type
    override fun getMetadata(): Map<String, String> = metadata
}

interface ContentFilter {
    fun filterContent(content: Content): String 
}

class EmailContentFilter : ContentFilter {
    override fun filterContent(content: Content): String {
        var filteredText = content.getText()
        val audience = content.getMetadata().getOrDefault("audience", "general")

        if (audience == "public") {
            filteredText = filteredText.replace(Regex("(?i)confidential:"), "[REDACTED]")
            filteredText = filteredText.replace(Regex("(?i)internal:"), "[REDACTED]")
        }

        return removePersonalInfo(filteredText)
    }

