public class ShippingService {
    private final OrderRepository orderRepository;
    private final InventoryService inventoryService;
    private final Logger logger;
    private final ShippingCostCalculator shippingCostCalculator;

    public ShippingService(OrderRepository orderRepository,
                           InventoryService inventoryService,
                           Logger logger,
                           ShippingCostCalculator shippingCostCalculator) {
        this.orderRepository = orderRepository;
        this.inventoryService = inventoryService;
        this.logger = logger;
        this.shippingCostCalculator = shippingCostCalculator;
    }

    public ShipmentResult processShipment(String orderId) {
        Order order = orderRepository.findById(orderId);
        if (order == null || !OrderStatus.PAID.equals(order.getStatus())) {
            return new ShipmentResult(false, "Invalid order or not paid", null);
        }

        for (OrderItem item : order.getItems()) {
            if (!inventoryService.isInStock(item.getProductId(), item.getQuantity())) {
                return new ShipmentResult(false, "Product out of stock: " + item.getProductId(), null);
            }
            inventoryService.reserve(item.getProductId(), item.getQuantity());
