        this.customerId = customerId;
        this.items = items;
        this.country = country;
    }

    public string GetCustomerId()
    {
        return customerId;
    }

    public List<OrderItem> GetItems()
    {
        return items;
    }

    public string GetCountry()
    {
        return country;
    }
}

public class OrderItem
{
    private string productId;
    private double price;
    private int quantity;
    private string category;

    public OrderItem(string productId, double price, int quantity, string category)
    {
        this.productId = productId;
        this.price = price;
        this.quantity = quantity;
        this.category = category;
    }

    public string GetProductId()
    {
        return productId;
    }

    public double GetPrice()
    {
        return price;
    }

    public int GetQuantity()
    {
        return quantity;
    }

    public string GetCategory()
    {
        return category;
    }
}

public interface ITaxCalculator
{
    double CalculateTax(Order order);
}

public class USTaxCalculator : ITaxCalculator
{
    private Dictionary<string, double> stateTaxRates = new Dictionary<string, double>();
    private Dictionary<string, double> categoryTaxRates = new Dictionary<string, double>();

    public USTaxCalculator()
    {
        InitializeStateTaxRates();
        InitializeCategoryTaxRates();
    }

    private void InitializeStateTaxRates()
    {
        stateTaxRates.Add("CA", 0.0725);
        stateTaxRates.Add("NY", 0.045);
        stateTaxRates.Add("TX", 0.0625);
    }

    private void InitializeCategoryTaxRates()
    {
        categoryTaxRates.Add("luxury", 0.03);
        categoryTaxRates.Add("food", 0.0);
    }

    public double CalculateTax(Order order)
    {
        string state = order.GetCustomerId().Substring(0, 2);
        double totalTax = 0.0;

        foreach (OrderItem item in order.GetItems())
        {
            double itemPrice = item.GetPrice() * item.GetQuantity();
            double stateTaxRate = stateTaxRates.ContainsKey(state) ? stateTaxRates[state] : 0.05;
            double categoryRate = categoryTaxRates.ContainsKey(item.GetCategory()) ? categoryTaxRates[item.GetCategory()] : 0.0;
            totalTax += itemPrice * (stateTaxRate + categoryRate);
        }

        return totalTax;
    }
}

public class EUTaxCalculator : ITaxCalculator
{
    private Dictionary<string, double> countryVatRates = new Dictionary<string, double>();

    public EUTaxCalculator()
    {
        InitializeCountryVatRates();
    }

    private void InitializeCountryVatRates()
    {
        countryVatRates.Add("DE", 0.19);
        countryVatRates.Add("FR", 0.20);
        countryVatRates.Add("IT", 0.22);
    }

    public double CalculateTax(Order order)
    {
        string countryCode = order.GetCustomerId().Substring(0, 2);
        double totalTax = 0.0;

        foreach (OrderItem item in order.GetItems())
        {
            double itemPrice = item.GetPrice() * item.GetQuantity();
            double vatRate = countryVatRates.ContainsKey(countryCode) ? countryVatRates[countryCode] : 0.20;
            totalTax += itemPrice * vatRate;
        }

        return totalTax;
    }
}

public class CATaxCalculator : ITaxCalculator
{
    private Dictionary<string, double> provinceTaxRates = new Dictionary<string, double>();

    public CATaxCalculator()
    {
        InitializeProvinceTaxRates();
    }

    private void InitializeProvinceTaxRates()
    {
        provinceTaxRates.Add("ON", 0.08);
        provinceTaxRates.Add("BC", 0.07);
        provinceTaxRates.Add("AB", 0.0);
    }

    public double CalculateTax(Order order)
    {
        string province = order.GetCustomerId().Substring(0, 2);
        double totalTax = 0.0;

        foreach (OrderItem item in order.GetItems())
        {
            double itemPrice = item.GetPrice() * item.GetQuantity();
            double provinceTaxRate = provinceTaxRates.ContainsKey(province) ? provinceTaxRates[province] : 0.0;
            totalTax += itemPrice * (provinceTaxRate + 0.13); // Adding the federal tax rate of Canada
        }

        return totalTax;
    }
}

public class TaxCalculatorFactory
{
    public static ITaxCalculator GetTaxCalculator(string country)
    {
        switch (country.ToUpper())
        {
            case "US":
                return new USTaxCalculator();
            case "EU":
                return new EUTaxCalculator();
            case "CA":
                return new CATaxCalculator();
            default:
                throw new ArgumentException("Unsupported country");
        }
    }
}

public class TaxService
{
    public double CalculateTax(Order order)
    {
        ITaxCalculator taxCalculator = TaxCalculatorFactory.GetTaxCalculator(order.GetCountry());
        return taxCalculator.CalculateTax(order);
    }
}

