using System;
using System.Collections.Generic;

public class Order
{
    private string id;
    private List<OrderItem> items;
    private Address shippingAddress;
    private string shippingMethod;
    private OrderStatus status;
    private double totalAmount;

    public Order(string id, List<OrderItem> items, Address shippingAddress, string shippingMethod)
    {
        this.id = id;
        this.items = items;
        this.shippingAddress = shippingAddress;
        this.shippingMethod = shippingMethod;
        this.status = OrderStatus.CREATED;
        CalculateTotalAmount();
    }

    private void CalculateTotalAmount()
    {
        totalAmount = 0.0;
        foreach (OrderItem item in items)
        {
            totalAmount += item.GetPrice() * item.GetQuantity();
        }
    }

    public string GetId()
    {
        return id;
    }

    public List<OrderItem> GetItems()
    {
        return items;
    }

    public Address GetShippingAddress()
    {
        return shippingAddress;
    }

    public string GetShippingMethod()
    {
        return shippingMethod;
    }

    public OrderStatus GetStatus()
    {
        return status;
    }

    public void SetStatus(OrderStatus status)
    {
        this.status = status;
    }

    public double GetTotalAmount()
    {
        return totalAmount;
    }
}

public class OrderItem
{
    private string productId;
    private int quantity;
    private double price;

    public OrderItem(string productId, int quantity, double price)
    {
        this.productId = productId;
        this.quantity = quantity;
        this.price = price;
    }

    public string GetProductId()
    {
        return productId;
    }

    public int GetQuantity()
    {
        return quantity;
    }

    public double GetPrice()
    {
        return price;
    }
}

public class Address
{
    private string name;
    private string street;
    private string city;
    private string state;
    private string country;
    private string zipCode;

    public Address(string name, string street, string city, string state, string country, string zipCode)
    {
        this.name = name;
        this.street = street;
        this.city = city;
        this.state = state;
        this.country = country;
        this.zipCode = zipCode;
    }

    public string GetName()
    {
        return name;
    }

    public string GetStreet()
    {
        return street;
    }

    public string GetCity()
    {
        return city;
    }

    public string GetState()
    {
        return state;
    }

    public string GetCountry()
    {
        return country;
    }

    public string GetZipCode()
    {
        return zipCode;
    }
}

public class Shipment
{
    private string orderId;
    private DateTime shipmentDate;
    private double cost;

    public Shipment(string orderId, DateTime shipmentDate)
    {
        this.orderId = orderId;
        this.shipmentDate = shipmentDate;
    }

    public string GetOrderId()
    {
        return orderId;
    }

    public DateTime GetShipmentDate()
    {
        return shipmentDate;
    }

    public double GetCost()
    {
        return cost;
    }

    public void SetCost(double cost)
    {
        this.cost = cost;
    }
}

public class ShippingCost
{
    private double cost;

    public ShippingCost(double cost)
    {
        this.cost = cost;
    }

    public double GetCost()
    {
        return cost;
    }
}

public class ShipmentResult
{
    private bool success;
    private string message;
    private Shipment shipment;

    public ShipmentResult(bool success, string message, Shipment shipment)
    {
        this.success = success;
        this.message = message;
        this.shipment = shipment;
    }

    public bool IsSuccess()
    {
        return success;
    }

    public string GetMessage()
    {
        return message;
    }

    public Shipment GetShipment()
    {
        return shipment;
    }
}

