class JobExecutor(private val taskRepository: TaskRepository, private val taskStatusUpdater: TaskStatusUpdater, private val failureNotifier: FailureNotifier) {
    fun executePendingJobs() {
        // Fetch pending tasks from the repository
        val tasks = taskRepository.getPendingTasks()

        for (task in tasks) {
            try {
                // Execute the task and update its status if successful
                val success = executeTask(task)
                if (success) {
                    taskStatusUpdater.updateTaskStatus(task, "COMPLETED")
                } else {
                    taskStatusUpdater.updateTaskStatus(task, "FAILED")
                    failureNotifier.sendFailureNotification(task, "Task failed to execute")
                }
            } catch (e: Exception) {
                // Handle exceptions and update the task status accordingly
                taskStatusUpdater.updateTaskStatus(task, "ERROR")
                failureNotifier.sendFailureNotification(task, e.message ?: "Unknown error occurred")
            }
        }
    }

    private fun executeTask(task: ScheduledTask): Boolean {
        // Execute the task based on its type and return true if successful, false otherwise
        when (task.type) {
            "DATA_SYNC" -> DataSyncTaskExecutor().executeTask(task)
            "REPORT_GENERATION" -> ReportGenerationTaskExecutor().executeTask(task)
            else -> throw IllegalArgumentException("Unsupported task type: ${task.type}")
        }
    }
}

