import java.sql.Connection
import java.sql.DriverManager
import javax.mail.internet.InternetAddress
import javax.mail.internet.MimeMessage
import javax.mail.Session
import java.util.Properties

data class Product(val id: String, val name: String, val price: Double)
data class CartItem(val product: Product, val quantity: Int)
data class ShoppingCart(val items: List<CartItem>)
data class Order(val id: String, val items: List<CartItem>, val totalAmount: Double, val customerEmail: String)

class CheckoutService(private val dbUrl: String, private val paymentGatewayUrl: String, private val smtpHost: String) {

    fun processCheckout(cart: ShoppingCart, customerEmail: String): Order? {
        println("[CheckoutService] Starting checkout process...")

        if (!validateCart(cart)) {
            println("[CheckoutService] Cart validation failed.")
            return null
        }
        println("[CheckoutService] Cart validated.")

        if (!checkInventory(cart)) {
            println("[CheckoutService] Inventory check failed.")
            return null
        }
        println("[CheckoutService] Inventory checked.")

        val totalAmount = calculateTotal(cart)
        println("[CheckoutService] Total amount calculated: $totalAmount")

        if (!processPayment(customerEmail, totalAmount)) {
            println("[CheckoutService] Payment processing failed.")

            return null
        }
        println("[CheckoutService] Payment processed.")

        val order = saveOrder(cart, totalAmount, customerEmail)
        if (order == null) {
            println("[CheckoutService] Failed to save order.")

            return null
        }
        println("[CheckoutService] Order saved: ${order.id}")

        updateInventory(cart)
        println("[CheckoutService] Inventory updated.")

        sendOrderConfirmationEmail(customerEmail, order)
        println("[CheckoutService] Order confirmation email sent.")

        println("[CheckoutService] Checkout process completed successfully.")
        return order
    }

    private fun validateCart(cart: ShoppingCart): Boolean {
        println("[CheckoutService] Validating cart...")
        return cart.items.isNotEmpty()
    }

    private fun checkInventory(cart: ShoppingCart): Boolean {
        println("[CheckoutService] Checking inventory via $paymentGatewayUrl...")

        return true
    }

    private fun calculateTotal(cart: ShoppingCart): Double {
        println("[CheckoutService] Calculating total...")
        return cart.items.sumByDouble { it.product.price * it.quantity }
    }

    private fun processPayment(email: String, amount: Double): Boolean {
        println("[CheckoutService] Processing payment for $amount via $paymentGatewayUrl...")

        return true
    }

    private fun saveOrder(cart: ShoppingCart, totalAmount: Double, customerEmail: String): Order? {
        println("[CheckoutService] Saving order to DB $dbUrl...")
        var conn: Connection? = null
        try {
            conn = DriverManager.getConnection(dbUrl)

            val orderId = "ORDER_${System.currentTimeMillis()}"

