using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading;

public class TaskProcessingEngine
{
    private readonly ITaskQueue _taskQueue;
    private readonly IDictionary<string, Action<QueuedTask>> _taskHandlers;

    public TaskProcessingEngine(ITaskQueue taskQueue)
    {
        _taskQueue = taskQueue;
        _taskHandlers = new Dictionary<string, Action<QueuedTask>>();
    }

    public void RegisterHandler(string taskType, Action<QueuedTask> handler)
    {
        if (_taskHandlers.ContainsKey(taskType))
            throw new ArgumentException($"A handler for task type '{taskType}' is already registered.");

        _taskHandlers[taskType] = handler;
    }

    public void StartProcessing(CancellationToken cancellationToken)
    {
        Console.WriteLine("[TaskProcessingEngine] Starting processing engine...");

        while (!cancellationToken.IsCancellationRequested)
        {
            try
            {
                var task = _taskQueue.Dequeue();
                if (task == null)
                {
                    Console.WriteLine("[TaskProcessingEngine] No tasks to process.");
                    Thread.Sleep(100); // Wait a bit before checking again
                    continue;
                }

                Console.WriteLine($"[TaskProcessingEngine] Processing task '{task.Type}'...");

                if (_taskHandlers.TryGetValue(task.Type, out var handler))
                {
                    try
                    {
                        handler(task);
                        LogProcessedTask(task, true);
                    }
                    catch (Exception ex)
                    {
                        Console.WriteLine($"[TaskProcessingEngine] Error processing task '{task.Type}': {ex.Message}");
                        LogProcessedTask(task, false);
                    }
                }
                else
                {
                    Console.WriteLine($"[TaskProcessingEngine] No handler registered for task type '{task.Type}'.");
                    LogProcessedTask(task, false);
                }
            }
            catch (Exception ex)
            {
                Console.WriteLine($"[TaskProcessingEngine] Error processing tasks: {ex.Message}");
            }
        }
    }

    private void LogProcessedTask(QueuedTask task, bool success)
    {
        var status = success ? "Successfully processed" : "Failed to process";
        Console.WriteLine($"[TaskProcessingEngine] {status} task '{task.Type}'.");
    }
}
