import java.util.Date

data class ScheduledTask(val id: String, val type: String, val payload: String, var status: String, var lastRun: Date?)

class JobExecutor(private val dbUrl: String, private val smtpHost: String) {

    fun executePendingJobs() {
        println("[JobExecutor] Starting execution of pending jobs.")

        val pendingTasks = fetchPendingTasks()
        println("[JobExecutor] Fetched ${pendingTasks.size} pending tasks.")

        if (pendingTasks.isEmpty()) {
            println("[JobExecutor] No pending tasks to execute.")
            return
        }

        for (task in pendingTasks) {
            println("[JobExecutor] Executing task: ${task.id} (${task.type})")
            task.status = "RUNNING"
            updateTaskStatus(task)

            try {
                val success = executeTask(task)
                if (success) {
                    task.status = "COMPLETED"
                    println("[JobExecutor] Task ${task.id} completed successfully.")
                } else {
                    task.status = "FAILED"
                    println("[JobExecutor] Task ${task.id} failed execution.")
                    sendFailureNotification(task, "Task execution returned false")
                }
            } catch (e: Exception) {
                task.status = "FAILED"
                println("[JobExecutor] Task ${task.id} failed with exception: ${e.message}")
                sendFailureNotification(task, "Exception: ${e.message}")
            }
            task.lastRun = Date()
            updateTaskStatus(task)
        }

        println("[JobExecutor] Finished execution of pending jobs.")
    }


    private fun fetchPendingTasks(): List<ScheduledTask> {
        println("[JobExecutor] Fetching pending tasks from DB $dbUrl...")
        var conn: Connection? = null
        val tasks = mutableListOf<ScheduledTask>()
        try {
