        Console.WriteLine("[OrderFulfillmentService] Inventory reserved.");

        var shippingLabel = GenerateShippingLabel(order);
        if (string.IsNullOrEmpty(shippingLabel))
        {
             Console.WriteLine("[OrderFulfillmentService] Shipping label generation failed.");
             ReleaseInventory(order); 
