        }
        Console.WriteLine("[OrderFulfillmentService] Shipping label generated.");

        if (!UpdateOrderStatus(orderId, "SHIPPED"))
        {
            Console.WriteLine("[OrderFulfillmentService] Failed to update order status.");
            ReleaseInventory(order);

            return false;
        }
        Console.WriteLine("[OrderFulfillmentService] Order status updated to SHIPPED.");

        SendShippingNotification(order.CustomerEmail, shippingLabel);
