using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading;

public class TaskProcessingEngine
{
    private readonly ITaskQueue _taskQueue;
    private readonly IDictionary<string, Action<string>> _taskHandlers;

    public TaskProcessingEngine(ITaskQueue taskQueue)
    {
        _taskQueue = taskQueue;
        _taskHandlers = new Dictionary<string, Action<string>>
        {
            { "ProcessOrder", HandleProcessOrderTask },
            { "SendEmail", HandleSendEmailTask }

        };
    }

    public void StartProcessing(CancellationToken cancellationToken)
    {
        Console.WriteLine("[TaskProcessingEngine] Starting processing engine...");

        while (!cancellationToken.IsCancellationRequested)
        {
            try
            {
                var task = _taskQueue.Dequeue();
                if (task == null)
                {
                    Console.WriteLine("[TaskProcessingEngine] No tasks in queue. Waiting...");
                    Thread.Sleep(1000);
                    continue;
                }

                Console.WriteLine($"[TaskProcessingEngine] Dequeued task type: {task.Type}, Payload: {task.Payload}");

                if (_taskHandlers.TryGetValue(task.Type, out var handler))
                {
                    try
                    {
                        Console.WriteLine($"[TaskProcessingEngine] Executing handler for task type: {task.Type}");
                        handler.Invoke(task.Payload);
                        Console.WriteLine($"[TaskProcessingEngine] Task {task.Type} executed successfully.");
                        LogProcessedTask(task.Id, task.Type, true, "Success");
                    }
                    catch (Exception ex)
                    {
                        Console.WriteLine($"[TaskProcessingEngine] Error executing task {task.Type}: {ex.Message}");
                        LogProcessedTask(task.Id, task.Type, false, $"Execution Error: {ex.Message}");

                    }
                }
                else
                {
                    Console.WriteLine($"[TaskProcessingEngine] No handler found for task type: {task.Type}");
                    LogProcessedTask(task.Id, task.Type, false, "No Handler Found");
                }
            }
            catch (Exception ex)
            {\n                Console.WriteLine($"[TaskProcessingEngine] Error dequeuing task: {ex.Message}");
                Thread.Sleep(5000);
            }
        }

        Console.WriteLine("[TaskProcessingEngine] Processing engine stopped.");
    }

    private void HandleProcessOrderTask(string payload)
    {
        Console.WriteLine($"[HandleProcessOrderTask] Processing order with payload: {payload}");

    }

    private void HandleSendEmailTask(string payload)
    {
         Console.WriteLine($"[HandleSendEmailTask] Sending email with payload: {payload}");

    }

    private void LogProcessedTask(Guid taskId, string taskType, bool success, string resultDetails)
    {
        Console.WriteLine($"[LogProcessedTask] LOG: TaskId={taskId}, Type={taskType}, Success={success}, Details='{resultDetails}'");

    }
}

public interface ITaskQueue
{
    QueuedTask Dequeue();
    void Enqueue(QueuedTask task);
}

public class InMemoryTaskQueue : ITaskQueue
{
    private readonly Queue<QueuedTask> _tasks = new Queue<QueuedTask>();

    public void Enqueue(QueuedTask task)
    {
        lock (_tasks)
        {
            _tasks.Enqueue(task);
            Console.WriteLine($"[InMemoryTaskQueue] Enqueued task {task.Type}.");
        }
    }

    public QueuedTask Dequeue()
    {
        lock (_tasks)
        {
            if (_tasks.Any())
            {
                return _tasks.Dequeue();
            }
            return null;
        }
    }
}

public class QueuedTask
{
    public Guid Id { get; set; }
    public string Type { get; set; }
    public string Payload { get; set; }
}

