public class AccountManager
{
    private readonly string _dbConnectionString;

    public AccountManager(string dbConnectionString)
    {
        _dbConnectionString = dbConnectionString;
    }

    public bool CreateUserAccount(string username, string password, string role, string permissionsJson)
    {
        Console.WriteLine($"[AccountManager] Attempting to create account for {username}");

        if (UserExists(username))
        {
            Console.WriteLine("[AccountManager] User {username} already exists.");
            LogAudit("CreateAccount", username, "User Exists", false);
            return false;
        }
        Console.WriteLine("[AccountManager] User does not exist.");

        string passwordHash = HashPassword(password);
        Console.WriteLine("[AccountManager] Password hashed.");

        if (!SaveUserToDatabase(username, passwordHash, role, permissionsJson))
        {
            Console.WriteLine("[AccountManager] Failed to save user {username} to database.");
            LogAudit("CreateAccount", username, "DB Save Failed", false);
            return false;
        }
        Console.WriteLine("[AccountManager] User {username} saved to database.");

        LogAudit("CreateAccount", username, "Success", true);
        Console.WriteLine($"[AccountManager] Account created for {username} with role {role}.");
        return true;
    }

    public bool AssignRoleToUser(string username, string newRole)
    {
        Console.WriteLine($"[AccountManager] Attempting to assign role {newRole} to {username}");

        if (!UserExists(username))
        {
            Console.WriteLine("[AccountManager] User {username} not found for role assignment.");
            LogAudit("AssignRole", username, "User Not Found", false);
            return false;
        }
        Console.WriteLine("[AccountManager] User found.");

        if (!UpdateUserRoleInDatabase(username, newRole))
        {
            Console.WriteLine("[AccountManager] Failed to update role for {username} in database.");
            LogAudit("AssignRole", username, "DB Update Failed", false);
            return false;
        }
        Console.WriteLine("[AccountManager] Role updated for {username} to {newRole}.");

        LogAudit("AssignRole", username, $"Role changed to {newRole}", true);
        Console.WriteLine($"[AccountManager] Role {newRole} assigned to {username}.");
        return true;
    }

    private bool UserExists(string username)
    {
