public class OrderFulfillmentService
{
    private readonly string _dbConnectionString;
    private readonly string _shippingApiEndpoint;
    private readonly string _warehouseInventoryEndpoint;

    public OrderFulfillmentService(string dbConnectionString, string shippingApiEndpoint, string warehouseInventoryEndpoint)
    {
        _dbConnectionString = dbConnectionString;
        _shippingApiEndpoint = shippingApiEndpoint;
        _warehouseInventoryEndpoint = warehouseInventoryEndpoint;
    }

