        var order = GetOrderDetails(orderId);
        if (order == null)
        {
            Console.WriteLine("[OrderFulfillmentService] Order not found.");
            return false;
        }
        Console.WriteLine("[OrderFulfillmentService] Order details fetched.");

        if (!ReserveInventory(order))
        {
            Console.WriteLine("[OrderFulfillmentService] Inventory reservation failed.");
            return false;
        }
        Console.WriteLine("[OrderFulfillmentService] Inventory reserved.");

        var shippingLabel = GenerateShippingLabel(order);
        if (string.IsNullOrEmpty(shippingLabel))
        {
             Console.WriteLine("[OrderFulfillmentService] Shipping label generation failed.");
             ReleaseInventory(order); 
             return false;
        }
        Console.WriteLine("[OrderFulfillmentService] Shipping label generated.");

        if (!UpdateOrderStatus(orderId, "SHIPPED"))
        {
            Console.WriteLine("[OrderFulfillmentService] Failed to update order status.");
            ReleaseInventory(order);

            return false;
        }
        Console.WriteLine("[OrderFulfillmentService] Order status updated to SHIPPED.");

        SendShippingNotification(order.CustomerEmail, shippingLabel);
        Console.WriteLine("[OrderFulfillmentService] Shipping notification sent.");

        Console.WriteLine($"[OrderFulfillmentService] Order ID: {orderId} fulfilled successfully.");
        return true;
    }

    private Order GetOrderDetails(int orderId)
    {
        Console.WriteLine($"[GetOrderDetails] Fetching details for order {orderId} from DB {_dbConnectionString}...");
        using (var conn = new SqlConnection(_dbConnectionString))
        {

        }
        return new Order { Id = orderId, CustomerEmail = "customer@example.com" };
    }

    private bool ReserveInventory(Order order)
    {
        Console.WriteLine($"[ReserveInventory] Reserving inventory for order {order.Id} via warehouse service at {_warehouseInventoryEndpoint}...");

        return true;
    }

     private bool ReleaseInventory(Order order)
    {
        Console.WriteLine($"[ReleaseInventory] Releasing inventory for order {order.Id} via warehouse service at {_warehouseInventoryEndpoint}...");

        return true;
    }

    private string GenerateShippingLabel(Order order)
    {
        Console.WriteLine($"[GenerateShippingLabel] Generating label for order {order.Id} via shipping API at {_shippingApiEndpoint}...");

        return $"ShippingLabel_{order.Id}.pdf";
    }

    private bool UpdateOrderStatus(int orderId, string status)
    {
        Console.WriteLine($"[UpdateOrderStatus] Updating status for order {orderId} to {status} in DB {_dbConnectionString}...");
        using (var conn = new SqlConnection(_dbConnectionString))
        {

        }
        return true;
    }

    private void SendShippingNotification(string customerEmail, string shippingLabelPath)
    {
        Console.WriteLine($"[SendShippingNotification] Sending notification to {customerEmail} with label {shippingLabelPath}...");

    }

