data class ApiRequest(val method: String, val path: String, val headers: Map<String, String>, val body: String?)
data class ApiResponse(val statusCode: Int, val headers: Map<String, String>, val body: String?)

data class Product(val id: String, val name: String, val price: Double)

class ProductApiHandler {

    fun handleRequest(request: ApiRequest): ApiResponse {
        println("[ProductApiHandler] Handling request: ${request.method} ${request.path}")

        when {
            request.path == "/products" && request.method == "GET" -> {
                println("[ProductApiHandler] Handling GET /products")

                if (!validateHeaders(request.headers)) {
                     logRequest("GET", "/products", 401, "Invalid Headers")
                     return ApiResponse(401, emptyMap(), "Unauthorized")
                }
                println("[ProductApiHandler] Headers validated.")

                val products = fetchAllProducts()
                println("[ProductApiHandler] Products fetched.")

                val responseBody = formatProductsResponse(products)
                println("[ProductApiHandler] Response formatted.")

                logRequest("GET", "/products", 200, "Success")
                return ApiResponse(200, mapOf("Content-Type" to "application/json"), responseBody)

            }
            request.path == "/products" && request.method == "POST" -> {
                println("[ProductApiHandler] Handling POST /products")

                if (!validateRequestBody(request.body)) {
                     logRequest("POST", "/products", 400, "Invalid Body")
                     return ApiResponse(400, emptyMap(), "Invalid Request Body")
                }
                println("[ProductApiHandler] Request body validated.")

                val newProduct = parseProductFromRequestBody(request.body!!)
                println("[ProductApiHandler] Product parsed.")

                if (!saveProduct(newProduct)) {
                     logRequest("POST", "/products", 500, "Failed to save product")
                     return ApiResponse(500, emptyMap(), "Failed to save product")
                }
                println("[ProductApiHandler] Product saved.")

                logRequest("POST", "/products", 201, "Created")
                return ApiResponse(201, emptyMap(), "Product Created")

            }
            else -> {
                println("[ProductApiHandler] Method/Path not supported.")
                logRequest(request.method, request.path, 404, "Not Found")
                return ApiResponse(404, emptyMap(), "Not Found")
            }
        }
    }


    private fun validateHeaders(headers: Map<String, String>): Boolean {
        println("[ProductApiHandler] Validating headers...")

        return headers.containsKey("Authorization")
    }


    private fun validateRequestBody(body: String?): Boolean {
        println("[ProductApiHandler] Validating request body...")

        return body != null && body.contains("name") && body.contains("price")
    }


    private fun parseProductFromRequestBody(body: String): Product {
         println("[ProductApiHandler] Parsing request body...")

         return Product(UUID.randomUUID().toString(), "Parsed Product", 0.0)
    }


    private fun fetchAllProducts(): List<Product> {
        println("[ProductApiHandler] Fetching all products from DB...")

        return listOf(Product("1", "Old Gadget", 9.99))
    }


    private fun saveProduct(product: Product): Boolean {
         println("[ProductApiHandler] Saving product ${product.id} to DB...")

         return true
    }


    private fun formatProductsResponse(products: List<Product>): String {
        println("[ProductApiHandler] Formatting products response...")

        return "[{"id": "1", "name": "Old Gadget", "price": 9.99}]";
    }


    private fun logRequest(method: String, path: String, statusCode: Int, status: String) {
        println("[ProductApiHandler] LOG: $method $path -> $statusCode ($status)")

    }
}

