import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.util.UUID;
import javax.mail.*;
import javax.mail.internet.*;
import java.util.Properties;

public class UserOnboardingService {

    private final String dbUrl;
    private final String smtpHost;

    public UserOnboardingService(String dbUrl, String smtpHost) {
        this.dbUrl = dbUrl;
        this.smtpHost = smtpHost;
    }

    public boolean onboardNewUser(String username, String email, String password) {
        System.out.println("[UserOnboardingService] Starting onboarding for user: " + username);

        if (userExists(username, email)) {
            System.out.println("[UserOnboardingService] User already exists.");
            logEvent("UserRegistrationFailed", username, "User already exists");
            return false;
        }
        System.out.println("[UserOnboardingService] User does not exist.");

        String userId = UUID.randomUUID().toString();
        String passwordHash = hashPassword(password);
        System.out.println("[UserOnboardingService] Password hashed.");

        if (!saveUserToDatabase(userId, username, email, passwordHash)) {
            System.out.println("[UserOnboardingService] Failed to save user to database.");
            logEvent("UserRegistrationFailed", username, "DB Save Failed");
            return false;
        }
        System.out.println("[UserOnboardingService] User saved with ID: " + userId);

        if (!sendVerificationEmail(email, userId)) {
            System.out.println("[UserOnboardingService] Failed to send verification email.");
            logEvent("UserRegistrationFailed", username, "Email Sending Failed");

            return false;
        }
        System.out.println("[UserOnboardingService] Verification email sent.");

        initializeUserProfile(userId);
        System.out.println("[UserOnboardingService] User profile initialized.");

        logEvent("UserRegistered", username, "Success");
        System.out.println("[UserOnboardingService] Onboarding complete for user: " + username);
        return true;
    }

    private boolean userExists(String username, String email) {
        System.out.println("[UserOnboardingService] Checking if user exists (DB: " + dbUrl + ")...");
        String sql = "SELECT COUNT(*) FROM users WHERE username = ? OR email = ?";
        try (Connection conn = DriverManager.getConnection(dbUrl);
             PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.setString(1, username);
            pstmt.setString(2, email);
            ResultSet rs = pstmt.executeQuery();
            if (rs.next()) {
                return rs.getInt(1) > 0;
            }
            return false;
        } catch (SQLException e) {
            System.err.println("[UserOnboardingService] Database error checking existence: " + e.getMessage());
            return false;
        }
    }

    private String hashPassword(String password) {
        System.out.println("[UserOnboardingService] Hashing password...");

        return "hashed_" + password; 
    }

    private boolean saveUserToDatabase(String userId, String username, String email, String passwordHash) {
        System.out.println("[UserOnboardingService] Saving user to DB: " + dbUrl + "...");
        String sql = "INSERT INTO users (id, username, email, password_hash) VALUES (?, ?, ?, ?)";
        try (Connection conn = DriverManager.getConnection(dbUrl);
             PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.setString(1, userId);
            pstmt.setString(2, username);
            pstmt.setString(3, email);
            pstmt.setString(4, passwordHash);
            int affectedRows = pstmt.executeUpdate();
            return affectedRows > 0;
        } catch (SQLException e) {
            System.err.println("[UserOnboardingService] Database error saving user: " + e.getMessage());
            return false;
        }
    }

    private boolean sendVerificationEmail(String email, String userId) {
        System.out.println("[UserOnboardingService] Sending verification email to " + email + " (SMTP: " + smtpHost + ")...");
        Properties props = new Properties();
        props.put("mail.smtp.auth", "true");
        props.put("mail.smtp.starttls.enable", "true");
        props.put("mail.smtp.host", smtpHost);
        props.put("mail.smtp.port", "587");

        Session session = Session.getInstance(props, new Authenticator() {
            protected PasswordAuthentication getPasswordAuthentication() {
                return new PasswordAuthentication("no-reply@example.com", "password"); 
            }
        });

        try {
            Message message = new MimeMessage(session);
            message.setFrom(new InternetAddress("no-reply@example.com"));
            message.setRecipients(Message.RecipientType.TO, InternetAddress.parse(email));
            message.setSubject("Verify Your Email Address");
            message.setText("Please click the link to verify your email: http:


            Transport.send(message);
            return true;
        } catch (MessagingException e) {
            System.err.println("[UserOnboardingService] Error sending email: " + e.getMessage());
            return false;
        }
    }

    private void initializeUserProfile(String userId) {
        System.out.println("[UserOnboardingService] Initializing profile for user ID: " + userId + "...");

        String sql = "INSERT INTO user_profiles (user_id, registration_date) VALUES (?, CURRENT_DATE)";
        try (Connection conn = DriverManager.getConnection(dbUrl);
             PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.setString(1, userId);
            pstmt.executeUpdate();
        } catch (SQLException e) {
            System.err.println("[UserOnboardingService] Database error initializing profile: " + e.getMessage());

        }
    }

    private void logEvent(String eventType, String relatedUser, String details) {
        System.out.println("[UserOnboardingService] LOG: Type=" + eventType + ", User=" + relatedUser + ", Details=" + details);


    }

    public static void main(String[] args) {
        String testDbUrl = "jdbc:sqlite:test_onboarding.db";
        String testSmtpHost = "localhost";

        try (Connection conn = DriverManager.getConnection(testDbUrl)) {
            conn.createStatement().execute("DROP TABLE IF EXISTS users;");
            conn.createStatement().execute("CREATE TABLE users (id TEXT PRIMARY KEY, username TEXT UNIQUE, email TEXT UNIQUE, password_hash TEXT);");
            conn.createStatement().execute("DROP TABLE IF EXISTS user_profiles;");
            conn.createStatement().execute("CREATE TABLE user_profiles (user_id TEXT PRIMARY KEY, registration_date DATE);");
             conn.createStatement().execute("DROP TABLE IF EXISTS audit_log;");
            conn.createStatement().execute("CREATE TABLE audit_log (event_type TEXT, related_user TEXT, details TEXT);");
        } catch (SQLException e) {
            e.printStackTrace();
        }

        UserOnboardingService service = new UserOnboardingService(testDbUrl, testSmtpHost);

        service.onboardNewUser("johndoe", "john.doe@example.com", "securepass");
        service.onboardNewUser("johndoe", "john.doe@example.com", "anotherpass"); 
        service.onboardNewUser("janedoe", "jane.doe@example.com", "easypass");
    }
}

