        _shippingApiEndpoint = shippingApiEndpoint;
        _warehouseInventoryEndpoint = warehouseInventoryEndpoint;
    }

    public bool FulfillOrder(int orderId)
    {
        Console.WriteLine($"[OrderFulfillmentService] Starting fulfillment for Order ID: {orderId}");

        var order = GetOrderDetails(orderId);
        if (order == null)
        {
            Console.WriteLine("[OrderFulfillmentService] Order not found.");
            return false;
        }
        Console.WriteLine("[OrderFulfillmentService] Order details fetched.");

        if (!ReserveInventory(order))
        {
            Console.WriteLine("[OrderFulfillmentService] Inventory reservation failed.");
