import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.util.ArrayList;
import java.util.List;

public class DataMigrationService {

    private final String sourceDbUrl;
    private final String targetDbUrl;

    public DataMigrationService(String sourceDbUrl, String targetDbUrl) {
        this.sourceDbUrl = sourceDbUrl;
        this.targetDbUrl = targetDbUrl;
        initializeTargetDb();
    }

    private void initializeTargetDb() {
        System.out.println("[DataMigrationService] Initializing target DB " + targetDbUrl + "...");
        String sql = "CREATE TABLE IF NOT EXISTS migrated_data (id INTEGER PRIMARY KEY, value TEXT)";
        try (Connection conn = DriverManager.getConnection(targetDbUrl);
             PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.executeUpdate();
        } catch (SQLException e) {
            System.err.println("[DataMigrationService] Error initializing target DB: " + e.getMessage());
        }
    }

    public int migrateData() {
        System.out.println("[DataMigrationService] Starting data migration from " + sourceDbUrl + " to " + targetDbUrl);

        List<SourceData> sourceRecords = fetchSourceData();
        if (sourceRecords == null || sourceRecords.isEmpty()) {
            System.out.println("[DataMigrationService] No data found in source DB.");
            logMigrationEvent("Migration", "No data in source", 0, false);
            return 0;
        }
        System.out.println("[DataMigrationService] Fetched " + sourceRecords.size() + " records from source.");

        List<TargetData> targetRecords = transformData(sourceRecords);
        if (targetRecords == null || targetRecords.isEmpty()) {
            System.out.println("[DataMigrationService] No data after transformation.");
            logMigrationEvent("Migration", "No data after transformation", sourceRecords.size(), false);
            return 0;
        }
        System.out.println("[DataMigrationService] Transformed " + targetRecords.size() + " records.");

        int importedCount = saveTargetData(targetRecords);
        System.out.println("[DataMigrationService] Saved " + importedCount + " records to target.");

        if (importedCount == targetRecords.size()) {
            logMigrationEvent("Migration", "Success", importedCount, true);
             System.out.println("[DataMigrationService] Data migration complete. " + importedCount + " records migrated.");
        } else {
             logMigrationEvent("Migration", "Partial Success or Failure", importedCount, false);
             System.out.println("[DataMigrationService] Data migration complete with issues. " + importedCount + " records migrated out of " + targetRecords.size() + ".");
        }

        return importedCount;
    }

    private List<SourceData> fetchSourceData() {
        System.out.println("[DataMigrationService] Fetching data from source DB " + sourceDbUrl + "...");
        List<SourceData> records = new ArrayList<>();
        String sql = "SELECT id, raw_value FROM source_data WHERE processed = FALSE LIMIT 100";
        try (Connection conn = DriverManager.getConnection(sourceDbUrl);
             PreparedStatement pstmt = conn.prepareStatement(sql);
             ResultSet rs = pstmt.executeQuery()) {

             records.add(new SourceData(1, "value1"));
             records.add(new SourceData(2, "value2"));

        } catch (SQLException e) {
            System.err.println("[DataMigrationService] Database error fetching source data: " + e.getMessage());
            return null;
        }
        return records;
    }

    private List<TargetData> transformData(List<SourceData> sourceRecords) {
        System.out.println("[DataMigrationService] Transforming data...");
        List<TargetData> targetRecords = new ArrayList<>();
        for (SourceData record : sourceRecords) {
            if (record.rawValue != null && record.rawValue.length() > 2) {
                targetRecords.add(new TargetData(record.id, record.rawValue.toUpperCase()));
            }
        }
        return targetRecords;
    }

    private int saveTargetData(List<TargetData> targetRecords) {
        System.out.println("[DataMigrationService] Saving data to target DB " + targetDbUrl + "...");
        String sql = "INSERT INTO migrated_data (id, value) VALUES (?, ?)";
        int importedCount = 0;
        try (Connection conn = DriverManager.getConnection(targetDbUrl);
             PreparedStatement pstmt = conn.prepareStatement(sql)) {

            for (TargetData record : targetRecords) {
                pstmt.setInt(1, record.id);
                pstmt.setString(2, record.value);
                pstmt.addBatch();
            }
            int[] results = pstmt.executeBatch();
            for (int count : results) {
                if (count >= 0) { 
                    importedCount += count; 
                }
            }

        } catch (SQLException e) {
            System.err.println("[DataMigrationService] Database error saving target data: " + e.getMessage());

        }
        return importedCount;
    }

    private void logMigrationEvent(String eventType, String details, int count, boolean success) {
        System.out.println("[DataMigrationService] LOG: Type=" + eventType + ", Details=" + details + ", Count=" + count + ", Success=" + success);


    }

    static class SourceData {
        int id;
        String rawValue;
        SourceData(int id, String rawValue) { this.id = id; this.rawValue = rawValue; }
    }

    static class TargetData {
        int id;
        String value;
        TargetData(int id, String value) { this.id = id; this.value = value; }
    }

    public static void main(String[] args) {
        String sourceDbUrl = "jdbc:sqlite:source_migration.db";
        String targetDbUrl = "jdbc:sqlite:target_migration.db";

         try (Connection conn = DriverManager.getConnection(sourceDbUrl)) {
            conn.createStatement().execute("DROP TABLE IF EXISTS source_data;");
            conn.createStatement().execute("CREATE TABLE source_data (id INTEGER PRIMARY KEY, raw_value TEXT, processed BOOLEAN);");
            conn.createStatement().execute("INSERT INTO source_data (id, raw_value, processed) VALUES (1, 'value1', FALSE);");
            conn.createStatement().execute("INSERT INTO source_data (id, raw_value, processed) VALUES (2, 'value2', FALSE);");
            conn.createStatement().execute("INSERT INTO source_data (id, raw_value, processed) VALUES (3, 'a', FALSE);"); 
             conn.createStatement().execute("INSERT INTO source_data (id, raw_value, processed) VALUES (4, NULL, FALSE);"); 
        } catch (SQLException e) {
            e.printStackTrace();
        }

         new java.io.File("target_migration.db").delete();

        DataMigrationService migrator = new DataMigrationService(sourceDbUrl, targetDbUrl);

        migrator.migrateData();

    }
}

