class Resume {
    String content;
    public Resume(String content) {
        this.content = content;
    }
}

interface PortalUser {
    void searchJobs();
    void applyForJob(Resume resume);
    void postJob(String jobDescription);
    void reviewApplicants();
    void manageSiteSettings();
}

class JobSeeker implements PortalUser {
    @Override
    public void searchJobs() {
        System.out.println("Searching for jobs...");
    }

    @Override
    public void applyForJob(Resume resume) {
        System.out.println("Applied with resume: " + resume.content);
    }

    @Override
    public void postJob(String jobDescription) {
        throw new UnsupportedOperationException("Job seekers can't post jobs.");
    }

    @Override
    public void reviewApplicants() {
        throw new UnsupportedOperationException("Job seekers can't review applicants.");
    }

    @Override
    public void manageSiteSettings() {
        throw new UnsupportedOperationException("Job seekers can't manage site settings.");
    }
}

class Recruiter implements PortalUser {
    @Override
    public void searchJobs() {
        throw new UnsupportedOperationException("Recruiters don't search for jobs.");
    }

    @Override
    public void applyForJob(Resume resume) {
        throw new UnsupportedOperationException("Recruiters don't apply for jobs.");
    }

    @Override
    public void postJob(String jobDescription) {
        System.out.println("Job posted: " + jobDescription);
    }

    @Override
    public void reviewApplicants() {
        System.out.println("Reviewing applicants...");
    }

    @Override
    public void manageSiteSettings() {
        throw new UnsupportedOperationException("Recruiters can't manage site settings.");
    }
}

class Administrator implements PortalUser {
    @Override
    public void searchJobs() {
        throw new UnsupportedOperationException("Admins don't search for jobs.");
    }

    @Override
    public void applyForJob(Resume resume) {
        throw new UnsupportedOperationException("Admins don't apply for jobs.");
    }

    @Override
    public void postJob(String jobDescription) {
        throw new UnsupportedOperationException("Admins don't post jobs.");
    }

    @Override
    public void reviewApplicants() {
        throw new UnsupportedOperationException("Admins don't review applicants.");
    }

    @Override
    public void manageSiteSettings() {
        System.out.println("Managing site settings...");
    }
}

