enum FlightStatus
{
    SCHEDULED, BOARDING, DEPARTED, LANDED, CANCELLED, DELAYED
}

enum PaymentMethod
{
    CREDIT_CARD, DEBIT_CARD, PAYPAL
}

class Flight
{
    private string flightNumber;
    private string origin;
    private string destination;
    private DateTime departureTime;
    private DateTime arrivalTime;
    private FlightStatus status;

    public Flight(string flightNumber, string origin, string destination, 
                 DateTime departureTime, DateTime arrivalTime)
    {
        this.flightNumber = flightNumber;
        this.origin = origin;
        this.destination = destination;
        this.departureTime = departureTime;
        this.arrivalTime = arrivalTime;
        this.status = FlightStatus.SCHEDULED;
    }

    public string GetFlightNumber() { return flightNumber; }
    public string GetOrigin() { return origin; }
    public string GetDestination() { return destination; }
    public DateTime GetDepartureTime() { return departureTime; }
    public DateTime GetArrivalTime() { return arrivalTime; }
    public FlightStatus GetStatus() { return status; }

    public void SetStatus(FlightStatus status) { this.status = status; }
}

class Booking
{
    private string bookingId;
    private string passengerId;
    private string flightNumber;
    private DateTime bookingTime;

    public Booking(string bookingId, string passengerId, string flightNumber)
    {
        this.bookingId = bookingId;
        this.passengerId = passengerId;
        this.flightNumber = flightNumber;
        this.bookingTime = DateTime.Now;
    }

    public string GetBookingId() { return bookingId; }
    public string GetPassengerId() { return passengerId; }
    public string GetFlightNumber() { return flightNumber; }
    public DateTime GetBookingTime() { return bookingTime; }

    public override string ToString()
    {
        return "Booking{" +
               "bookingId='" + bookingId + '\'' +
               ", passengerId='" + passengerId + '\'' +
               ", flightNumber='" + flightNumber + '\'' +
               ", bookingTime=" + bookingTime.ToString("o") +
               '}';
    }
}
