interface MusicPlayer {
    void play();
    void pause();
    void stop();
    void skipTrack();
    void eject();
}

class CDPlayer implements MusicPlayer {
    public void play() {
        System.out.println("CD is playing.");
    }
    public void pause() {
        System.out.println("CD is paused.");
    }
    public void stop() {
        System.out.println("CD stopped.");
    }
    public void skipTrack() {
        System.out.println("Skipping to next CD track.");
    }
    public void eject() {
        System.out.println("CD ejected.");
    }
}

class VinylPlayer implements MusicPlayer {
    public void play() {
        System.out.println("Vinyl record is playing.");
    }
    public void pause() {
        System.out.println("Vinyl playback paused (lift the needle).");
    }
    public void stop() {
        System.out.println("Vinyl playback stopped.");
    }

    public void skipTrack() {
        System.out.println("Can't skip track on a vinyl record!");
    }

    public void eject() {
        System.out.println("Can't eject a vinyl record like a CD!");
    }
}

