interface DeviceControl {
    void turnOn();
    void turnOff();
    void volumeUp();
    void volumeDown();
    void lock();
}

class LightBulb {
    protected boolean isOn;

    public void turnOn() {
        isOn = true;
        System.out.println("LightBulb turned on.");
    }

    public void turnOff() {
        isOn = false;
        System.out.println("LightBulb turned off.");
    }
}

class RegularLightBulb extends LightBulb implements DeviceControl {
    @Override
    public void volumeUp() {
        System.out.println("Volume up not supported on RegularLightBulb.");
    }

    @Override
    public void volumeDown() {
        System.out.println("Volume down not supported on RegularLightBulb.");
    }

    @Override
    public void lock() {
        System.out.println("Lock not supported on RegularLightBulb.");
    }
}

class RGBLightBulb extends LightBulb implements DeviceControl {
    private String color = "White";

    public void changeColor(String newColor) {
        color = newColor;
        System.out.println("RGBLightBulb color changed to " + color);
    }

    @Override
    public void volumeUp() {
        System.out.println("Volume up not supported on RGBLightBulb.");
    }

    @Override
    public void volumeDown() {
        System.out.println("Volume down not supported on RGBLightBulb.");
    }

    @Override
    public void lock() {
        System.out.println("Lock not supported on RGBLightBulb.");
    }
}

class TV implements DeviceControl {
    private int volume = 10;
    private boolean isOn = false;

    @Override
    public void turnOn() {
        isOn = true;
        System.out.println("TV turned on.");
    }

    @Override
    public void turnOff() {
        isOn = false;
        System.out.println("TV turned off.");
    }

    @Override
    public void volumeUp() {
        volume++;
        System.out.println("TV volume increased to " + volume);
    }

    @Override
    public void volumeDown() {
        volume--;
        System.out.println("TV volume decreased to " + volume);
    }

    @Override
    public void lock() {
        System.out.println("TV locked (parental control enabled).");
    }
}

class Speaker implements DeviceControl {
    private int volume = 5;
    private boolean isOn = false;

    @Override
    public void turnOn() {
        isOn = true;
        System.out.println("Speaker turned on.");
    }

    @Override
    public void turnOff() {
        isOn = false;
        System.out.println("Speaker turned off.");
    }

    @Override
    public void volumeUp() {
        volume++;
        System.out.println("Speaker volume increased to " + volume);
    }

    @Override
    public void volumeDown() {
        volume--;
        System.out.println("Speaker volume decreased to " + volume);
    }

    @Override
    public void lock() {
        System.out.println("Speaker locked.");
    }
}

class SmartLock implements DeviceControl {
    private boolean isLocked = false;

    @Override
    public void turnOn() {
        System.out.println("SmartLock doesn't need to be turned on.");
    }

    @Override
    public void turnOff() {
        System.out.println("SmartLock doesn't need to be turned off.");
    }

    @Override
    public void volumeUp() {
        System.out.println("Volume control not supported on SmartLock.");
    }

    @Override
    public void volumeDown() {
        System.out.println("Volume control not supported on SmartLock.");
    }

    @Override
    public void lock() {
        isLocked = true;
        System.out.println("SmartLock is now locked.");
    }
}

