public interface Shape {
    double calculateArea();
    double calculatePerimeter();
    double calculateVolume();
}

public class Circle implements Shape {
    private double radius;

    public Circle(double radius) {
        this.radius = radius;
    }

    @Override
    public double calculateArea() {
        return Math.PI * radius * radius;
    }

    @Override
    public double calculatePerimeter() {
        return 2 * Math.PI * radius;
    }

    @Override
    public double calculateVolume() {
        System.out.println("Warning: Calculating volume for a 2D shape doesn't make sense!");
        return 0.0;
    }
}

public class Rectangle implements Shape {
    private double width;
    private double height;

    public Rectangle(double width, double height) {
        this.width = width;
        this.height = height;
    }

    @Override
    public double calculateArea() {
        return width * height;
    }

    @Override
    public double calculatePerimeter() {
        return 2 * (width + height);
    }

    @Override
    public double calculateVolume() {
        System.out.println("Warning: Calculating volume for a 2D shape doesn't make sense!");
        return 0.0;
    }
}

public class Line implements Shape {
    private double length;

    public Line(double length) {
        this.length = length;
    }

    @Override
    public double calculateArea() {
        System.out.println("Warning: Calculating area for a 1D shape doesn't make sense!");
        return 0.0;
    }

    @Override
    public double calculatePerimeter() {
        return length;
    }

    @Override
    public double calculateVolume() {
        System.out.println("Warning: Calculating volume for a 1D shape doesn't make sense!");
        return 0.0;
    }
}

public class Cube implements Shape {
    private double side;

    public Cube(double side) {
        this.side = side;
    }

    @Override
    public double calculateArea() {
        return 6 * side * side;
    }

    @Override
    public double calculatePerimeter() {
        return 12 * side;
    }

    @Override
    public double calculateVolume() {
        return side * side * side;
    }
}

