interface NetworkDevice {
    fun connect()
    fun transferData(data: String)
    fun runDiagnostics()
    fun updateFirmware(version: String)
    fun getDeviceName(): String
    fun displayInfo()
}

interface MultimediaDevice {
    fun powerOn()
    fun play()
    fun stop()
    fun setVolume(level: Int)
    fun setScreenResolution(resolution: String)
    fun getDeviceName(): String
    fun displayInfo()
}

class Router(private val name: String, private val ipAddress: String) : NetworkDevice {
    override fun connect() {
        println("Router $name established connection.")
    }

    override fun transferData(data: String) {
        println("Router transferring data: $data")
    }

    override fun runDiagnostics() {
        println("Running diagnostics on router $name")
    }

    override fun updateFirmware(version: String) {
        println("Router firmware updated to version: $version")
    }

    override fun getDeviceName(): String = name

    override fun displayInfo() {
        println("Router: $name, IP: $ipAddress")
    }
}

class NetworkSwitch(private val name: String, private val ipAddress: String) : NetworkDevice {
    override fun connect() {
        println("Switch $name established connection.")
    }

    override fun transferData(data: String) {
        println("Switch transferring data: $data")
    }

    override fun runDiagnostics() {
        println("Running diagnostics on switch $name")
    }

    override fun updateFirmware(version: String) {
        println("Switch firmware updated to version: $version")
    }

    override fun getDeviceName(): String = name

    override fun displayInfo() {
        println("Network Switch: $name, IP: $ipAddress")
    }
}

class SmartTV(private val name: String, private val location: String) : MultimediaDevice {
    override fun powerOn() {
        println("$name TV powered on.")
    }

    override fun play() {
        println("$name TV playing content.")
    }

    override fun stop() {
        println("$name TV stopped playback.")
    }

    override fun setVolume(level: Int) {
        println("$name TV volume set to $level")
    }

    override fun setScreenResolution(resolution: String) {
        println("$name TV resolution set to $resolution")
    }

    override fun getDeviceName(): String = name

    override fun displayInfo() {
        println("Smart TV: $name, Location: $location")
    }
}

class SmartSpeaker(private val name: String, private val location: String) : MultimediaDevice {
    override fun powerOn() {
        println("$name speaker powered on.")
    }

    override fun play() {
        println("$name speaker playing audio.")
    }

    override fun stop() {
        println("$name speaker stopped playback.")
    }

    override fun setVolume(level: Int) {
        println("$name speaker volume set to $level")
    }

    override fun setScreenResolution(resolution: String) {
        println("ERROR: $name speaker doesn't have a screen!")
    }

    override fun getDeviceName(): String = name

    override fun displayInfo() {
        println("Smart Speaker: $name, Location: $location")
    }
}

class DeviceManager {
    fun configureDevice(device: Any) {
        when (device) {
            is NetworkDevice -> {
                device.displayInfo()
                device.connect()
            }
            is MultimediaDevice -> {
                device.displayInfo()
                device.powerOn()
            }
        }
    }

    fun updateFirmware(device: NetworkDevice) {
        println("Updating firmware for ${device.getDeviceName()}")
        device.updateFirmware("v2.4.1")
    }

    fun playMedia(device: MultimediaDevice) {
        println("Playing media on ${device.getDeviceName()}")
        device.play()
    }

    fun adjustVolume(device: MultimediaDevice, level: Int) {
        println("Adjusting volume on ${device.getDeviceName()}")
        device.setVolume(level)
    }

    fun playMedia(device: NetworkDevice) {
        println("Attempting to play media on ${device.getDeviceName()}")
        println("ERROR: Network devices cannot play media!")
    }

    fun updateFirmware(device: MultimediaDevice) {
        println("Attempting to update firmware on ${device.getDeviceName()}")
        println("ERROR: Cannot update firmware on multimedia devices!")
    }
}

