using System;
using System.Collections.Generic;

interface IUIComponent
{
    void Render();
    void Resize(int width, int height);
    void SetVisible(bool visible);
    void SetBackground(string color);
    void SetForeground(string color);

    void OnClick(int x, int y);
    void OnHover(int x, int y);
    void OnKeyPress(int keyCode);
    void OnFocus();
    void OnBlur();

    void AddChild(IUIComponent child);
    void RemoveChild(IUIComponent child);
    IUIComponent GetParent();
}

interface IUIStyleAndAnimation
{
    void SetFont(string fontName, int size);
    void SetTextAlignment(string alignment);
    void SetMargin(int top, int right, int bottom, int left);
    void SetPadding(int top, int right, int bottom, int left);

    void FadeIn(int durationMs);
    void FadeOut(int durationMs);
    void SlideIn(string direction, int durationMs);
    void SlideOut(string direction, int durationMs);
    void StopAnimations();
}

class Button : IUIComponent, IUIStyleAndAnimation
{
    private string label;
    private int x, y, width, height;
    private string backgroundColor;
    private string foregroundColor;
    private IUIComponent parent;

    public Button(string label, int x, int y, int width, int height)
    {
        this.label = label;
        this.x = x;
        this.y = y;
        this.width = width;
        this.height = height;
        this.backgroundColor = "#CCCCCC";
        this.foregroundColor = "#000000";
    }

    public void Render()
    {
        Console.WriteLine("Rendering button: " + label);
    }

    public void OnClick(int x, int y)
    {
        Console.WriteLine("Button clicked: " + label);
    }

    public void SetBackground(string color)
    {
        this.backgroundColor = color;
    }

    public void SetForeground(string color)
    {
        this.foregroundColor = color;
    }

    public void Resize(int width, int height)
    {
        this.width = width;
        this.height = height;
    }
    public void SetVisible(bool visible) { }
    public void OnHover(int x, int y) { }
    public void OnKeyPress(int keyCode) { }
    public void OnFocus() { }
    public void OnBlur() { }
    public void AddChild(IUIComponent child) { }
    public void RemoveChild(IUIComponent child) { }
    public IUIComponent GetParent() { return parent; }

    public void SetFont(string fontName, int size) { }
    public void SetTextAlignment(string alignment) { }
    public void SetMargin(int top, int right, int bottom, int left) { }
    public void SetPadding(int top, int right, int bottom, int left) { }

    public void FadeIn(int durationMs) { }
    public void FadeOut(int durationMs) { }
    public void SlideIn(string direction, int durationMs) { }
    public void SlideOut(string direction, int durationMs) { }
    public void StopAnimations() { }
}

class TextField : IUIComponent, IUIStyleAndAnimation
{
    private string text;
    private int x, y, width, height;
    private bool focused;
    private IUIComponent parent;

    public TextField(string initialText, int x, int y, int width, int height)
    {
        this.text = initialText;
        this.x = x;
        this.y = y;
        this.width = width;
        this.height = height;
        this.focused = false;
    }

    public void Render()
    {
        Console.WriteLine("Rendering text field: " + text);
    }

    public void OnKeyPress(int keyCode)
    {
        Console.WriteLine("Key pressed in text field: " + keyCode);
    }

    public void OnFocus()
    {
        focused = true;
        Console.WriteLine("Text field focused");
    }

    public void OnBlur()
    {
        focused = false;
        Console.WriteLine("Text field lost focus");
    }

    public void Resize(int width, int height)
    {
        this.width = width;
        this.height = height;
    }
    public void SetVisible(bool visible) { }
