        self.location = location

    def power_on(self):
        print(f"{self.name} speaker powered on.")

    def play(self):
        print(f"{self.name} speaker playing audio.")

    def stop(self):
        print(f"{self.name} speaker stopped playback.")

    def set_volume(self, level):
        print(f"{self.name} speaker volume set to {level}")

    def set_screen_resolution(self, resolution):
        print(f"ERROR: {self.name} speaker doesn't have a screen!")

    def get_device_name(self):
        return self.name

    def display_info(self):
        print(f"Smart Speaker: {self.name}, Location: {self.location}")


class DeviceManager:
    def configure_device(self, device):
        if isinstance(device, NetworkDevice):
            device.display_info()
            device.connect()
        elif isinstance(device, MultimediaDevice):
            device.display_info()
            device.power_on()

    def update_firmware_network(self, device: NetworkDevice):
        print(f"Updating firmware for {device.get_device_name()}")
        device.update_firmware("v2.4.1")

    def play_media_multimedia(self, device: MultimediaDevice):
        print(f"Playing media on {device.get_device_name()}")
        device.play()

    def adjust_volume(self, device: MultimediaDevice, level: int):
        print(f"Adjusting volume on {device.get_device_name()}")
        device.set_volume(level)

    def play_media_network(self, device: NetworkDevice):
        print(f"Attempting to play media on {device.get_device_name()}")
        print("ERROR: Network devices cannot play media!")

    def update_firmware_multimedia(self, device: MultimediaDevice):
        print(f"Attempting to update firmware on {device.get_device_name()}")
        print("ERROR: Cannot update firmware on multimedia devices!")

