        throw UnsupportedOperationException("Admin system doesn't cancel bookings directly")
    }

    override fun getBookingDetails(bookingId: String): Booking? {
        throw UnsupportedOperationException("Admin system doesn't handle booking details")
    }

    override fun processPayment(bookingId: String, method: PaymentMethod, amount: Double): Boolean {
        throw UnsupportedOperationException("Admin system doesn't process payments")
    }

    override fun refundPayment(bookingId: String): Boolean {
        throw UnsupportedOperationException("Admin system doesn't handle refunds")
    }
