    }

    override fun sendCancellationNotice(bookingId: String, email: String) {
        throw UnsupportedOperationException("Reporting service doesn't send emails")
    }

    override fun generateBookingReport(bookingId: String): ByteArray {
        println("Generating booking report for $bookingId")
        return ByteArray(512)
    }

    override fun getBookingStatistics(start: LocalDateTime, end: LocalDateTime): Map<String, Int> {
        println("Generating booking statistics from $start to $end")
