using System;
using System.Collections.Generic;
using System.Text;

interface IFlightBookingService
{
    void AddFlight(Flight flight);
    void UpdateFlightStatus(string flightNumber, FlightStatus status);
    List<Flight> SearchAvailableFlights(string origin, string destination, DateTime date);

    Booking CreateBooking(string passengerId, string flightNumber);
    void CancelBooking(string bookingId);
    Booking GetBookingDetails(string bookingId);

    bool ProcessPayment(string bookingId, PaymentMethod method, double amount);
    bool RefundPayment(string bookingId);
}

interface INotificationService
{
    void SendBookingConfirmation(string bookingId, string email);
    void SendCancellationNotice(string bookingId, string email);

    byte[] GenerateBookingReport(string bookingId);
    Dictionary<string, int> GetBookingStatistics(DateTime start, DateTime end);
}

class CustomerBookingSystem : IFlightBookingService
{
    private Dictionary<string, Flight> flights = new Dictionary<string, Flight>();
    private Dictionary<string, Booking> bookings = new Dictionary<string, Booking>();

    public void SetFlightsForDemo(params Flight[] flightsToAdd)
    {
        foreach (Flight flight in flightsToAdd)
        {
            flights[flight.GetFlightNumber()] = flight;
        }
    }

    public void AddFlight(Flight flight)
    {
        throw new NotSupportedException("Customers cannot add flights");
    }

    public void UpdateFlightStatus(string flightNumber, FlightStatus status)
    {
        throw new NotSupportedException("Customers cannot update flight status");
    }

    public List<Flight> SearchAvailableFlights(string origin, string destination, DateTime date)
    {
        List<Flight> availableFlights = new List<Flight>();
        foreach (Flight flight in flights.Values)
        {
            if (flight.GetOrigin() == origin && 
                flight.GetDestination() == destination &&
                flight.GetDepartureTime().Date == date.Date)
            {
                availableFlights.Add(flight);
            }
        }
        return availableFlights;
    }

    public Booking CreateBooking(string passengerId, string flightNumber)
    {
        Flight flight;
        if (flights.TryGetValue(flightNumber, out flight))
        {
            string bookingId = "B" + DateTimeOffset.Now.ToUnixTimeMilliseconds().ToString();
            Booking booking = new Booking(bookingId, passengerId, flightNumber);
            bookings[bookingId] = booking;
            return booking;
        }
        return null;
    }

    public void CancelBooking(string bookingId)
    {
        bookings.Remove(bookingId);
    }

    public Booking GetBookingDetails(string bookingId)
    {
        Booking booking;
        bookings.TryGetValue(bookingId, out booking);
        return booking;
    }

    public bool ProcessPayment(string bookingId, PaymentMethod method, double amount)
    {
        Console.WriteLine("Processing payment of $" + amount + " for booking " + bookingId);
        return true;
    }

    public bool RefundPayment(string bookingId)
    {
        Console.WriteLine("Refunding payment for booking " + bookingId);
        return true;
    }
}

