class GameContext {
    private Movable movable;
    private Attackable attackable;
    private Displayable displayable;

    public GameContext(Movable movable, Attackable attackable, Displayable displayable) {
        this.movable = movable;
        this.attackable = attackable;
        this.displayable = displayable;
    }

    public void setCharacter(Movable newMovable, Attackable newAttackable, Displayable newDisplayable) {
        this.movable = newMovable;
        this.attackable = newAttackable;
        this.displayable = newDisplayable;
    }

    public void move() {
        movable.walk();
        if (movable instanceof Movable) {
            movable.fly();
        }
    }

    public void attack() {
        attackable.meleeAttack();
        if (attackable instanceof Attackable) {
            attackable.castSpell();
        }
    }

    public void display() {
        displayable.displayRole();
    }
}
