        println("Ranger moves swiftly.")
    }

    override fun fly() {
        println("Ranger cannot fly!")
    }

    override fun meleeAttack() {
        println("Ranger stabs with a dagger.")
    }

    override fun castSpell() {
        println("Ranger cannot cast spells!")
    }

    override fun displayRole() {
        println("Current character: Ranger")
    }
}

class GameContext(private var character: Character) {

    fun setCharacter(newCharacter: Character) {
        character = newCharacter
    }

    fun move() {
        (character as? Movement)?.walk()
        (character as? Movement)?.fly()
    }

    fun attack() {
        (character as? Attack)?.meleeAttack()
