    override fun cancelBooking(bookingId: String) {
        throw UnsupportedOperationException("Admin system doesn't cancel bookings directly")
    }

    override fun getBookingDetails(bookingId: String): Booking? {
        throw UnsupportedOperationException("Admin system doesn't handle booking details")
    }

    override fun processPayment(bookingId: String, method: PaymentMethod, amount: Double): Boolean {
        throw UnsupportedOperationException("Admin system doesn't process payments")
    }

    override fun refundPayment(bookingId: String): Boolean {
        throw UnsupportedOperationException("Admin system doesn't handle refunds")
    }
}

class EmailService : NotificationService {
    override fun sendBookingConfirmation(bookingId: String, email: String) {
        println("Sending booking confirmation email for $bookingId to $email")
    }

    override fun sendCancellationNotice(bookingId: String, email: String) {
        println("Sending cancellation notice for $bookingId to $email")
    }

    override fun generateBookingReport(bookingId: String): ByteArray {
        throw UnsupportedOperationException("Email service doesn't generate reports")
    }

    override fun getBookingStatistics(start: LocalDateTime, end: LocalDateTime): Map<String, Int> {
        throw UnsupportedOperationException("Email service doesn't generate statistics")
    }
}

class ReportingService : NotificationService {
    override fun sendBookingConfirmation(bookingId: String, email: String) {
        throw UnsupportedOperationException("Reporting service doesn't send emails")
    }

    override fun sendCancellationNotice(bookingId: String, email: String) {
        throw UnsupportedOperationException("Reporting service doesn't send emails")
    }

    override fun generateBookingReport(bookingId: String): ByteArray {
        println("Generating booking report for $bookingId")
        return ByteArray(512)
    }

    override fun getBookingStatistics(start: LocalDateTime, end: LocalDateTime): Map<String, Int> {
        println("Generating booking statistics from $start to $end")
        return mapOf("totalBookings" to 120, "cancelledBookings" to 15)
    }
}

enum class FlightStatus {
    SCHEDULED, BOARDING, DEPARTED, LANDED, CANCELLED, DELAYED
}

enum class PaymentMethod {
    CREDIT_CARD, DEBIT_CARD, PAYPAL
}

data class Flight(
    val flightNumber: String,
    val origin: String,
    val destination: String,
    val departureTime: LocalDateTime,
    val arrivalTime: LocalDateTime,
    var status: FlightStatus = FlightStatus.SCHEDULED
)

data class Booking(
