        {
            Console.WriteLine("Configuring network device.");
            // Additional configuration logic for network devices
        }
        else if (device is IMultimediaDevice multimediaDevice)
        {
            Console.WriteLine("Configuring multimedia device.");
            // Additional configuration logic for multimedia devices
        }
