using System;
using System.Collections.Generic;
using System.Text;

interface IFlightBookingService
{
    void AddFlight(Flight flight);
    void UpdateFlightStatus(string flightNumber, FlightStatus status);
    List<Flight> SearchAvailableFlights(string origin, string destination, DateTime date);

    Booking CreateBooking(string passengerId, string flightNumber);
    void CancelBooking(string bookingId);
    Booking GetBookingDetails(string bookingId);

    bool ProcessPayment(string bookingId, PaymentMethod method, double amount);
    bool RefundPayment(string bookingId);
}

interface INotificationService
{
    void SendBookingConfirmation(string bookingId, string email);
    void SendCancellationNotice(string bookingId, string email);

    byte[] GenerateBookingReport(string bookingId);
    Dictionary<string, int> GetBookingStatistics(DateTime start, DateTime end);
}

class CustomerBookingSystem : IFlightBookingService
{
    private Dictionary<string, Flight> flights = new Dictionary<string, Flight>();
    private Dictionary<string, Booking> bookings = new Dictionary<string, Booking>();

    public void SetFlightsForDemo(params Flight[] flightsToAdd)
    {
        foreach (Flight flight in flightsToAdd)
        {
            flights[flight.GetFlightNumber()] = flight;
        }
    }

    public void AddFlight(Flight flight)
    {
        throw new NotSupportedException("Customers cannot add flights");
    }

    public void UpdateFlightStatus(string flightNumber, FlightStatus status)
    {
        throw new NotSupportedException("Customers cannot update flight status");
    }

    public List<Flight> SearchAvailableFlights(string origin, string destination, DateTime date)
    {
        List<Flight> availableFlights = new List<Flight>();
        foreach (Flight flight in flights.Values)
        {
            if (flight.GetOrigin() == origin && 
                flight.GetDestination() == destination &&
                flight.GetDepartureTime().Date == date.Date)
            {
                availableFlights.Add(flight);
            }
        }
        return availableFlights;
    }

    public Booking CreateBooking(string passengerId, string flightNumber)
    {
        Flight flight;
        if (flights.TryGetValue(flightNumber, out flight))
        {
            string bookingId = "B" + DateTimeOffset.Now.ToUnixTimeMilliseconds().ToString();
            Booking booking = new Booking(bookingId, passengerId, flightNumber);
            bookings[bookingId] = booking;
            return booking;
        }
        return null;
    }

    public void CancelBooking(string bookingId)
    {
        bookings.Remove(bookingId);
    }

    public Booking GetBookingDetails(string bookingId)
    {
        Booking booking;
        bookings.TryGetValue(bookingId, out booking);
        return booking;
    }

    public bool ProcessPayment(string bookingId, PaymentMethod method, double amount)
    {
        Console.WriteLine("Processing payment of $" + amount + " for booking " + bookingId);
        return true;
    }

    public bool RefundPayment(string bookingId)
    {
        Console.WriteLine("Refunding payment for booking " + bookingId);
        return true;
    }
}

class AdminFlightSystem : IFlightBookingService
{
    private Dictionary<string, Flight> flights = new Dictionary<string, Flight>();

    public void AddFlight(Flight flight)
    {
        flights[flight.GetFlightNumber()] = flight;
    }

    public void UpdateFlightStatus(string flightNumber, FlightStatus status)
    {
        Flight flight;
        if (flights.TryGetValue(flightNumber, out flight))
        {
            flight.SetStatus(status);
        }
    }

    public List<Flight> SearchAvailableFlights(string origin, string destination, DateTime date)
    {
        List<Flight> availableFlights = new List<Flight>();
        foreach (Flight flight in flights.Values)
        {
            if (flight.GetOrigin() == origin && 
                flight.GetDestination() == destination &&
                flight.GetDepartureTime().Date == date.Date)
            {
                availableFlights.Add(flight);
            }
        }
        return availableFlights;
    }

    public Booking CreateBooking(string passengerId, string flightNumber)
    {
        throw new NotSupportedException("Admin system doesn't create bookings directly");
    }

    public void CancelBooking(string bookingId)
    {
        throw new NotSupportedException("Admin system doesn't cancel bookings directly");
    }

    public Booking GetBookingDetails(string bookingId)
    {
        throw new NotSupportedException("Admin system doesn't handle booking details");
    }

    public bool ProcessPayment(string bookingId, PaymentMethod method, double amount)
    {
        throw new NotSupportedException("Admin system doesn't process payments");
    }

    public bool RefundPayment(string bookingId)
    {
        throw new NotSupportedException("Admin system doesn't handle refunds");
    }
}

class EmailService : INotificationService
{
    public void SendBookingConfirmation(string bookingId, string email)
    {
        Console.WriteLine("Sending booking confirmation email for " + bookingId + " to " + email);
    }

    public void SendCancellationNotice(string bookingId, string email)
    {
        Console.WriteLine("Sending cancellation notice for " + bookingId + " to " + email);
    }

    public byte[] GenerateBookingReport(string bookingId)
    {
        throw new NotSupportedException("Email service doesn't generate reports");
    }

    public Dictionary<string, int> GetBookingStatistics(DateTime start, DateTime end)
    {
        throw new NotSupportedException("Email service doesn't generate statistics");
    }
}

class ReportingService : INotificationService
{
    public void SendBookingConfirmation(string bookingId, string email)
    {
        throw new NotSupportedException("Reporting service doesn't send emails");
    }

    public void SendCancellationNotice(string bookingId, string email)
    {
        throw new NotSupportedException("Reporting service doesn't send emails");
    }

    public byte[] GenerateBookingReport(string bookingId)
    {
        Console.WriteLine("Generating booking report for " + bookingId);
        return new byte[512]; 
    }

    public Dictionary<string, int> GetBookingStatistics(DateTime start, DateTime end)
    {
        Console.WriteLine("Generating booking statistics from " + start + " to " + end);
        Dictionary<string, int> stats = new Dictionary<string, int>();
        stats["totalBookings"] = 120;
        stats["cancelledBookings"] = 15;
        return stats;
    }
}

enum FlightStatus
{
    SCHEDULED, BOARDING, DEPARTED, LANDED, CANCELLED, DELAYED
}

enum PaymentMethod
{
    CREDIT_CARD, DEBIT_CARD, PAYPAL
}

class Flight
{
    private string flightNumber;
    private string origin;
    private string destination;
    private DateTime departureTime;
    private DateTime arrivalTime;
    private FlightStatus status;

    public Flight(string flightNumber, string origin, string destination, 
                 DateTime departureTime, DateTime arrivalTime)
    {
        this.flightNumber = flightNumber;
        this.origin = origin;
        this.destination = destination;
        this.departureTime = departureTime;
        this.arrivalTime = arrivalTime;
        this.status = FlightStatus.SCHEDULED;
    }

    public string GetFlightNumber() { return flightNumber; }
    public string GetOrigin() { return origin; }
    public string GetDestination() { return destination; }
    public DateTime GetDepartureTime() { return departureTime; }
    public DateTime GetArrivalTime() { return arrivalTime; }
    public FlightStatus GetStatus() { return status; }

    public void SetStatus(FlightStatus status) { this.status = status; }
}

class Booking
{
    private string bookingId;
    private string passengerId;
    private string flightNumber;
    private DateTime bookingTime;

    public Booking(string bookingId, string passengerId, string flightNumber)
    {
        this.bookingId = bookingId;
        this.passengerId = passengerId;
        this.flightNumber = flightNumber;
        this.bookingTime = DateTime.Now;
    }

    public string GetBookingId() { return bookingId; }
    public string GetPassengerId() { return passengerId; }
    public string GetFlightNumber() { return flightNumber; }
    public DateTime GetBookingTime() { return bookingTime; }

    public override string ToString()
    {
        return "Booking{" +
               "bookingId='" + bookingId + '\'' +
               ", passengerId='" + passengerId + '\'' +
               ", flightNumber='" + flightNumber + '\'' +
               ", bookingTime=" + bookingTime.ToString("o") +
               '}';
    }
}

