    @Override
    public void sendBookingConfirmation(String bookingId, String email) {
        throw new UnsupportedOperationException("Reporting service doesn't send emails");
    }

    @Override
    public void sendCancellationNotice(String bookingId, String email) {
        throw new UnsupportedOperationException("Reporting service doesn't send emails");
    }

    @Override
    public byte[] generateBookingReport(String bookingId) {
        System.out.println("Generating booking report for " + bookingId);
        return new byte[512]; 
    }

    @Override
    public Map<String, Integer> getBookingStatistics(LocalDateTime start, LocalDateTime end) {
        System.out.println("Generating booking statistics from " + start + " to " + end);
        Map<String, Integer> stats = new HashMap<>();
        stats.put("totalBookings", 120);
        stats.put("cancelledBookings", 15);
        return stats;
    }
}


