        return "Booking{" +
               "bookingId='" + bookingId + '\'' +
               ", passengerId='" + passengerId + '\'' +
               ", flightNumber='" + flightNumber + '\'' +
               ", bookingTime=" + bookingTime.format(DateTimeFormatter.ISO_LOCAL_DATE_TIME) +
               '}';
    }
}

