from abc import ABC, abstractmethod
from datetime import datetime, timedelta
from typing import List, Dict


class FlightStatus:
    SCHEDULED = "SCHEDULED"
    BOARDING = "BOARDING"
    DEPARTED = "DEPARTED"
    LANDED = "LANDED"
    CANCELLED = "CANCELLED"
    DELAYED = "DELAYED"

class PaymentMethod:
    CREDIT_CARD = "CREDIT_CARD"
    DEBIT_CARD = "DEBIT_CARD"
    PAYPAL = "PAYPAL"


class Flight:
    def __init__(self, flight_number, origin, destination, departure_time, arrival_time):
        self.flight_number = flight_number
        self.origin = origin
        self.destination = destination
        self.departure_time = departure_time
        self.arrival_time = arrival_time
        self.status = FlightStatus.SCHEDULED

    def __repr__(self):
        return f"Flight({self.flight_number})"

class Booking:
    def __init__(self, booking_id, passenger_id, flight_number):
        self.booking_id = booking_id
        self.passenger_id = passenger_id
        self.flight_number = flight_number
        self.booking_time = datetime.now()

    def __str__(self):
        return f"Booking{{bookingId='{self.booking_id}', passengerId='{self.passenger_id}', flightNumber='{self.flight_number}', bookingTime={self.booking_time.isoformat()}}}"


class FlightBookingService(ABC):
    @abstractmethod
    def add_flight(self, flight): pass
    @abstractmethod
    def update_flight_status(self, flight_number, status): pass
    @abstractmethod
    def search_available_flights(self, origin, destination, date): pass
    @abstractmethod
    def create_booking(self, passenger_id, flight_number): pass
    @abstractmethod
    def cancel_booking(self, booking_id): pass
    @abstractmethod
    def get_booking_details(self, booking_id): pass
    @abstractmethod
    def process_payment(self, booking_id, method, amount): pass
    @abstractmethod
    def refund_payment(self, booking_id): pass


class NotificationService(ABC):
    @abstractmethod
    def send_booking_confirmation(self, booking_id, email): pass
    @abstractmethod
    def send_cancellation_notice(self, booking_id, email): pass
    @abstractmethod
    def generate_booking_report(self, booking_id): pass
    @abstractmethod
    def get_booking_statistics(self, start, end): pass

class CustomerBookingSystem(FlightBookingService):
    def __init__(self):
        self.flights: Dict[str, Flight] = {}
        self.bookings: Dict[str, Booking] = {}

    def set_flights_for_demo(self, *flights):
        for f in flights:
            self.flights[f.flight_number] = f

    def add_flight(self, flight):
        raise NotImplementedError("Customers cannot add flights")

    def update_flight_status(self, flight_number, status):
        raise NotImplementedError("Customers cannot update flight status")

    def search_available_flights(self, origin, destination, date):
        return [
            f for f in self.flights.values()
            if f.origin == origin and f.destination == destination and f.departure_time.date() == date.date()
        ]

    def create_booking(self, passenger_id, flight_number):
        if flight_number in self.flights:
            booking_id = "B" + str(int(datetime.now().timestamp() * 1000))
            booking = Booking(booking_id, passenger_id, flight_number)
            self.bookings[booking_id] = booking
            return booking
        return None

    def cancel_booking(self, booking_id):
        self.bookings.pop(booking_id, None)

    def get_booking_details(self, booking_id):
        return self.bookings.get(booking_id)

    def process_payment(self, booking_id, method, amount):
        print(f"Processing payment of ${amount:.2f} for booking {booking_id}")
        return True

    def refund_payment(self, booking_id):
        print(f"Refunding payment for booking {booking_id}")
        return True


class AdminFlightSystem(FlightBookingService):
    def __init__(self):
        self.flights: Dict[str, Flight] = {}

    def add_flight(self, flight):
        self.flights[flight.flight_number] = flight

    def update_flight_status(self, flight_number, status):
        flight = self.flights.get(flight_number)
        if flight:
            flight.status = status

    def search_available_flights(self, origin, destination, date):
        return [
            f for f in self.flights.values()
            if f.origin == origin and f.destination == destination and f.departure_time.date() == date.date()
        ]

    def create_booking(self, passenger_id, flight_number):
        raise NotImplementedError("Admin system doesn't create bookings directly")

    def cancel_booking(self, booking_id):
        raise NotImplementedError("Admin system doesn't cancel bookings directly")

    def get_booking_details(self, booking_id):
        raise NotImplementedError("Admin system doesn't handle booking details")

    def process_payment(self, booking_id, method, amount):
        raise NotImplementedError("Admin system doesn't process payments")

    def refund_payment(self, booking_id):
        raise NotImplementedError("Admin system doesn't handle refunds")


class EmailService(NotificationService):
    def send_booking_confirmation(self, booking_id, email):
        print(f"Sending booking confirmation email for {booking_id} to {email}")

    def send_cancellation_notice(self, booking_id, email):
        print(f"Sending cancellation notice for {booking_id} to {email}")

    def generate_booking_report(self, booking_id):
        raise NotImplementedError("Email service doesn't generate reports")

    def get_booking_statistics(self, start, end):
        raise NotImplementedError("Email service doesn't generate statistics")


class ReportingService(NotificationService):
    def send_booking_confirmation(self, booking_id, email):
        raise NotImplementedError("Reporting service doesn't send emails")

    def send_cancellation_notice(self, booking_id, email):
        raise NotImplementedError("Reporting service doesn't send emails")

    def generate_booking_report(self, booking_id):
        print(f"Generating booking report for {booking_id}")
        return bytes(512)

    def get_booking_statistics(self, start, end):
        print(f"Generating booking statistics from {start} to {end}")
        return {"totalBookings": 120, "cancelledBookings": 15}

