    void start();
    void stop();
    void drive();
    void fly();
    void dive();
}

class Car implements Vehicle {
    @Override
    public void start() {
        System.out.println("Car started.");
    }

    @Override
    public void stop() {
        System.out.println("Car stopped.");
    }

    @Override
    public void drive() {
        System.out.println("Car is driving on the road.");
    }

    @Override
    public void fly() {
        throw new UnsupportedOperationException("Car cannot fly.");
    }

    @Override
    public void dive() {
        throw new UnsupportedOperationException("Car cannot dive.");
    }
